import React, { useState } from "react";
import axios from "axios";
import "../styles/ResumeBuilder.css";

const ResumeBuilder = () => {
  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,

    education: [{ institute: "", city: "", year: "", grade: "" }],
    experience: [{ role: "", company: "", duration: "", activities: "" }],

    projects: [
      {
        name: "",
        description: "",
        technologies: "",
        keyPoints: [""],
        link: "",
      },
    ],

    certificates: [{ title: "", issuedBy: "", issuedOn: "", credential: "" }],
    skills: [""],
  });

  const [summary, setSummary] = useState("");
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);

  // -------------------------
  // Generic Field Update
  // -------------------------
  const handleChange = (section, index, field, value) => {
    const updated = [...form[section]];
    updated[index][field] = value;
    setForm({ ...form, [section]: updated });
  };

  const handleKeyPointChange = (projectIndex, keyIndex, value) => {
    const updatedProjects = [...form.projects];
    updatedProjects[projectIndex].keyPoints[keyIndex] = value;
    setForm({ ...form, projects: updatedProjects });
  };

  const addKeyPoint = (projectIndex) => {
    const updatedProjects = [...form.projects];
    updatedProjects[projectIndex].keyPoints.push("");
    setForm({ ...form, projects: updatedProjects });
  };

  // Add new rows
  const addRow = (section, emptyTemplate) => {
    setForm({ ...form, [section]: [...form[section], emptyTemplate] });
  };

  // -------------------------
  // Summary Generator
  // -------------------------
  const generateSummary = async () => {
  try {
    setLoading(true);

    const token = localStorage.getItem("token");

    const prompt = `
      Write a professional resume summary for a ${form.role} named ${form.name}
      with ${form.totalExperienceYear} year(s) of experience. 
      Highlight skills in ${form.skills.join(", ")} under 100 words.
    `;

    const response = await fetch(
      "https://ai-resume-builder-backend-u4v2.onrender.com/ai/generate",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ prompt }),
      }
    );

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.message || "Failed to generate summary");
    }

    setSummary(data.summary);
  } catch (error) {
    console.error(error);
    alert("Error generating summary.");
  } finally {
    setLoading(false);
  }
};


  const clean = (v) => (v && v.trim() !== "" ? v.trim() : null);

  // -------------------------
  // Build Preview HTML
  // -------------------------
  const buildPreview = () => {
    return `
      <div style="font-family: Inter; line-height: 1.7;">
        <h1>${clean(form.name) || ""}</h1>
        <p>${clean(form.role) || ""}</p>

        <p>
          ${clean(form.emailId) ? `üìß ${form.emailId}<br/>` : ""}
          ${clean(form.phoneNo) ? `üìû ${form.phoneNo}<br/>` : ""}
          ${clean(form.linkedIn) ? `üîó ${form.linkedIn}<br/>` : ""}
          ${clean(form.portfolioLink) ? `üåê ${form.portfolioLink}<br/>` : ""}
        </p>

        ${
          summary
            ? `<h3>Professional Summary</h3><p>${summary}</p>`
            : ""
        }

        <h3>Education</h3>
        ${form.education
          .map(
            (e) => `
          <p>
            <b>${e.institute}</b> - ${e.city}<br/>
            ${e.year} | Grade: ${e.grade}
          </p>`
          )
          .join("")}

        <h3>Experience</h3>
        ${form.experience
          .map(
            (ex) => `
          <p>
            <b>${ex.role}</b> - ${ex.company}<br/>
            Duration: ${ex.duration}<br/>
            Activities: ${ex.activities}
          </p>`
          )
          .join("")}

        <h3>Projects</h3>
        ${form.projects
          .map(
            (p) => `
          <p>
            <b>${p.name}</b><br/>
            ${p.description}<br/>
            <i>Tech:</i> ${p.technologies}<br/>
            <ul>
              ${p.keyPoints
                .filter((kp) => kp.trim() !== "")
                .map((kp) => `<li>${kp}</li>`)
                .join("")}
            </ul>
            ${p.link ? `üîó ${p.link}` : ""}
          </p>`
          )
          .join("")}

        <h3>Certificates</h3>
        ${form.certificates
          .map(
            (c) => `
          <p>
            <b>${c.title}</b><br/>
            Issued By: ${c.issuedBy}<br/>
            Date: ${c.issuedOn}<br/>
            Credential: ${c.credential}
          </p>`
          )
          .join("")}
      </div>
    `;
  };

  const handlePreview = async () => {
    await generateSummary();
    setPreview(buildPreview());
  };

  // -------------------------
  // PDF Export
  // -------------------------
  const exportPDF = async () => {
    try {
      const response = await axios.post(
        "https://ai-resume-builder-backend-u4v2.onrender.com/api/pdf",
        { html: preview },
        { responseType: "blob" }
      );

      const blob = new Blob([response.data], { type: "application/pdf" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = `${form.name || "resume"}.pdf`;
      link.click();
    } catch (error) {
      alert("Error exporting PDF.");
    }
  };

  // ----------------------------------------------------------------
  //                     RENDER UI COMPONENTS
  // ----------------------------------------------------------------

  return (
    <div className="resume-wrapper">
      <h2>AI Resume Builder</h2>

      <div className="resume-section">
        <h3>Personal Details</h3>
        <div className="grid-2">
          <input placeholder="Full Name" value={form.name}
            onChange={(e) => setForm({ ...form, name: e.target.value })} />

          <input placeholder="Role / Title" value={form.role}
            onChange={(e) => setForm({ ...form, role: e.target.value })} />

          <input placeholder="Email ID" value={form.emailId}
            onChange={(e) => setForm({ ...form, emailId: e.target.value })} />

          <input placeholder="Phone Number" value={form.phoneNo}
            onChange={(e) => setForm({ ...form, phoneNo: e.target.value })} />

          <input placeholder="LinkedIn URL" value={form.linkedIn}
            onChange={(e) => setForm({ ...form, linkedIn: e.target.value })} />

          <input placeholder="Portfolio Link" value={form.portfolioLink}
            onChange={(e) => setForm({ ...form, portfolioLink: e.target.value })} />

          <input type="number" placeholder="Total Experience (Years)"
            value={form.totalExperienceYear}
            onChange={(e) =>
              setForm({ ...form, totalExperienceYear: Number(e.target.value) })
            }
          />
        </div>
      </div>

      {/* ------------------ EDUCATION ------------------ */}
      <div className="resume-section">
        <h3>Education</h3>
        {form.education.map((edu, i) => (
          <div key={i} className="grid-2">
            <input
              placeholder="Institute"
              value={edu.institute}
              onChange={(e) =>
                handleChange("education", i, "institute", e.target.value)
              }
            />
            <input
              placeholder="City"
              value={edu.city}
              onChange={(e) =>
                handleChange("education", i, "city", e.target.value)
              }
            />
            <input
              placeholder="Year"
              value={edu.year}
              onChange={(e) =>
                handleChange("education", i, "year", e.target.value)
              }
            />
            <input
              placeholder="Grade"
              value={edu.grade}
              onChange={(e) =>
                handleChange("education", i, "grade", e.target.value)
              }
            />
          </div>
        ))}
        <button
          className="btn-secondary"
          onClick={() =>
            addRow("education", {
              institute: "",
              city: "",
              year: "",
              grade: "",
            })
          }
        >
          + Add Education
        </button>
      </div>

      {/* ------------------ EXPERIENCE ------------------ */}
      <div className="resume-section">
        <h3>Experience</h3>
        {form.experience.map((ex, i) => (
          <div key={i} className="grid-2">
            <input
              placeholder="Role"
              value={ex.role}
              onChange={(e) =>
                handleChange("experience", i, "role", e.target.value)
              }
            />
            <input
              placeholder="Company"
              value={ex.company}
              onChange={(e) =>
                handleChange("experience", i, "company", e.target.value)
              }
            />
            <input
              placeholder="Duration"
              value={ex.duration}
              onChange={(e) =>
                handleChange("experience", i, "duration", e.target.value)
              }
            />
            <input
              placeholder="Activities"
              value={ex.activities}
              onChange={(e) =>
                handleChange("experience", i, "activities", e.target.value)
              }
            />
          </div>
        ))}
        <button
          className="btn-secondary"
          onClick={() =>
            addRow("experience", {
              role: "",
              company: "",
              duration: "",
              activities: "",
            })
          }
        >
          + Add Experience
        </button>
      </div>

      {/* ------------------ PROJECTS ------------------ */}
      <div className="resume-section">
        <h3>Projects</h3>
        {form.projects.map((p, i) => (
          <div key={i} className="project-block">
            <input
              placeholder="Project Name"
              value={p.name}
              onChange={(e) => handleChange("projects", i, "name", e.target.value)}
            />
            <input
              placeholder="Description"
              value={p.description}
              onChange={(e) =>
                handleChange("projects", i, "description", e.target.value)
              }
            />
            <input
              placeholder="Technologies"
              value={p.technologies}
              onChange={(e) =>
                handleChange("projects", i, "technologies", e.target.value)
              }
            />
            <input
              placeholder="Project Link"
              value={p.link}
              onChange={(e) => handleChange("projects", i, "link", e.target.value)}
            />

            <h4>Key Points</h4>
            {p.keyPoints.map((kp, kpIndex) => (
              <input
                key={kpIndex}
                placeholder="Key Point"
                value={kp}
                onChange={(e) =>
                  handleKeyPointChange(i, kpIndex, e.target.value)
                }
              />
            ))}

            <button className="btn-secondary" onClick={() => addKeyPoint(i)}>
              + Add Key Point
            </button>
          </div>
        ))}
        <button
          className="btn-secondary"
          onClick={() =>
            addRow("projects", {
              name: "",
              description: "",
              technologies: "",
              keyPoints: [""],
              link: "",
            })
          }
        >
          + Add Project
        </button>
      </div>

      {/* ------------------ CERTIFICATES ------------------ */}
      <div className="resume-section">
        <h3>Certificates</h3>
        {form.certificates.map((c, i) => (
          <div key={i} className="grid-2">
            <input
              placeholder="Certificate Title"
              value={c.title}
              onChange={(e) =>
                handleChange("certificates", i, "title", e.target.value)
              }
            />
            <input
              placeholder="Issued By"
              value={c.issuedBy}
              onChange={(e) =>
                handleChange("certificates", i, "issuedBy", e.target.value)
              }
            />
            <input
              placeholder="Issued On"
              value={c.issuedOn}
              onChange={(e) =>
                handleChange("certificates", i, "issuedOn", e.target.value)
              }
            />
            <input
              placeholder="Credential ID"
              value={c.credential}
              onChange={(e) =>
                handleChange("certificates", i, "credential", e.target.value)
              }
            />
          </div>
        ))}
        <button
          className="btn-secondary"
          onClick={() =>
            addRow("certificates", {
              title: "",
              issuedBy: "",
              issuedOn: "",
              credential: "",
            })
          }
        >
          + Add Certificate
        </button>
      </div>

      {/* ------------------ PREVIEW BUTTON ------------------ */}
      <div className="center-box">
        <button className="btn-primary" onClick={handlePreview} disabled={loading}>
          {loading ? "Generating..." : "Preview Resume"}
        </button>
      </div>

      {/* ------------------ PREVIEW ------------------ */}
      {preview && (
        <div className="preview-box">
          <h3>Preview</h3>
          <div
            className="preview-content"
            dangerouslySetInnerHTML={{ __html: preview }}
          />

          <button className="btn-secondary" onClick={exportPDF}>
            Export to PDF
          </button>
        </div>
      )}
    </div>
  );
};

export default ResumeBuilder;
