import React from "react";
import "../styles/PreviewPage.css"; // A4 styling

export default function PreviewPage({ data, close }) {
  return (
    <div className="modal-overlay">
      <div className="a4-container">
        <button className="close-btn" onClick={close}>X</button>

        {/* NAME */}
        <h1>{data.name}</h1>
        <p>{data.email}</p>
        <hr />

        {/* SUMMARY */}
        {data.summary && (
          <>
            <h2>Summary</h2>
            <p>{data.summary}</p>
            <hr />
          </>
        )}

        {/* SKILLS */}
        {data.skills?.length > 0 && (
          <>
            <h2>Skills</h2>
            <p>{data.skills.join(", ")}</p>
            <hr />
          </>
        )}

        {/* EDUCATION */}
        <h2>Education</h2>
        {data.education.map((edu, i) => (
          <div key={i} className="edu-block">
            <p className="edu-title">{edu.institute}</p>
            <p className="edu-sub">
              {edu.startYear} - {edu.endYear} | {edu.type} in {edu.department} |{" "}
              {edu.grade}
            </p>
          </div>
        ))}
        <hr />

        {/* EXPERIENCE */}
        <h2>Experience</h2>
        {data.experience.map((exp, i) => (
          <div key={i}>
            <p className="exp-title">{exp.role}</p>
            <p className="exp-sub">{exp.company}</p>
            <p className="exp-desc">{exp.activities}</p>
          </div>
        ))}
        <hr />

        {/* PROJECTS */}
        <h2>Projects</h2>
        {data.projects.map((p, i) => (
          <div key={i}>
            <p className="proj-title">{p.name}</p>
            <p className="proj-desc">{p.description}</p>
          </div>
        ))}
        <hr />

        {/* CERTIFICATES */}
        <h2>Certificates</h2>
        {data.certificates.map((c, i) => (
          <div key={i}>
            <p className="cert-title">{c.title}</p>
            <p className="cert-sub">{c.issuedBy}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
