import React, { useState } from "react";
import axios from "axios";

const ResumeBuilder = () => {
  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,
    education: [{ institute: "", city: "", year: "", grade: "" }],
    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [{ name: "", description: "", technologies: "", keyPoints: "", link: "" }],
    certificates: [{ title: "", issuedBy: "", issuedOn: "", credential: "" }],
    skills: [""],
  });

  const [summary, setSummary] = useState("");
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (section, index, field, value) => {
    if (Array.isArray(form[section])) {
      const updated = [...form[section]];
      if (field !== null) updated[index][field] = value;
      else updated[index] = value;
      setForm({ ...form, [section]: updated });
    } else {
      setForm({ ...form, [section]: value });
    }
  };

  const addField = (section, structure) => {
    setForm({ ...form, [section]: [...form[section], structure] });
  };

  const removeField = (section, index) => {
    const updated = [...form[section]];
    updated.splice(index, 1);
    setForm({ ...form, [section]: updated });
  };

  const generateSummary = async () => {
    try {
      setLoading(true);
      const prompt = `
      Write a professional resume summary for a ${form.role} named ${form.name}
      with ${form.totalExperienceYear} year of experience. Highlight skills in ${form.skills.join(",")},
      focusing on achievements and impact. Keep it under 100 words.
    `;
      const response = await axios.post(
        "https://ai-resume-builder-backend-u4v2.onrender.com/ai/generate",
        { prompt }
      );
      setSummary(response.data.summary);
    } catch (error) {
      console.error("AI Summary Error:", error);
      alert("Error generating summary.");
    } finally {
      setLoading(false);
    }
  };

  const clean = (text) => text && text.trim() !== "" ? text : null;

  const buildPreview = () => {
    return `
      <div style="font-family: Inter, sans-serif; padding: 20px; line-height: 1.6;">
        <h1 style="font-size: 24px; font-weight: bold;">${clean(form.name) || ""}</h1>
        <p style="font-size: 16px; color: #555;">${clean(form.role) || ""}</p>
        <p style="font-size: 14px; color: #555;">
          ${clean(form.emailId) ? `üìß ${form.emailId}<br/>` : ""}
          ${clean(form.phoneNo) ? `üìû ${form.phoneNo}<br/>` : ""}
          ${clean(form.linkedIn) ? `üîó ${form.linkedIn}<br/>` : ""}
          ${clean(form.portfolioLink) ? `üåê ${form.portfolioLink}` : ""}
        </p>
        ${form.totalExperienceYear > 0 ? `<p><b>Total Experience:</b> ${form.totalExperienceYear} Year(s)</p>` : ""}
        ${summary ? `<h3>Professional Summary</h3><p>${summary}</p>` : ""}
      </div>
    `;
  };

  const handlePreview = async () => {
    await generateSummary();
    const html = buildPreview();
    setPreview(html);
  };

  const exportPDF = async () => {
    try {
      const response = await axios.post(
        "https://ai-resume-builder-backend-u4v2.onrender.com/api/pdf",
        { html: preview },
        { responseType: "blob" }
      );
      const blob = new Blob([response.data], { type: "application/pdf" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = `${form.name || "resume"}.pdf`;
      link.click();
    } catch (error) {
      console.error("PDF export error:", error);
    }
  };

  return (
    <div className="container" style={{ padding: "20px", marginTop: "20px" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>AI Resume Builder</h2>

      {/* --- Basic Info --- */}
      <div className="form">
        <input placeholder="Full Name" value={form.name} onChange={(e) => handleChange("name", null, null, e.target.value)} />
        <input placeholder="Role / Title" value={form.role} onChange={(e) => handleChange("role", null, null, e.target.value)} />
        <input placeholder="Email ID" value={form.emailId} onChange={(e) => handleChange("emailId", null, null, e.target.value)} />
        <input placeholder="Phone Number" value={form.phoneNo} onChange={(e) => handleChange("phoneNo", null, null, e.target.value)} />
        <input placeholder="LinkedIn URL" value={form.linkedIn} onChange={(e) => handleChange("linkedIn", null, null, e.target.value)} />
        <input placeholder="Portfolio Link" value={form.portfolioLink} onChange={(e) => handleChange("portfolioLink", null, null, e.target.value)} />
        <input type="number" placeholder="Total Experience (in Years)" value={form.totalExperienceYear} onChange={(e) => handleChange("totalExperienceYear", null, null, Number(e.target.value))} />
      </div>

      {/* Buttons */}
      <div style={{ marginTop: "20px", textAlign: "center" }}>
        <button onClick={handlePreview} disabled={loading} style={{ padding: "8px 16px", cursor: "pointer" }}>
          {loading ? "Generating..." : "Preview Resume"}
        </button>
      </div>

      {/* Preview */}
      {preview && (
        <div style={{ marginTop: "20px" }}>
          <h3>Preview</h3>
          <div className="result" dangerouslySetInnerHTML={{ __html: preview }} />
          <div style={{ textAlign: "center", marginTop: "10px" }}>
            <button onClick={exportPDF} style={{ padding: "8px 16px", cursor: "pointer" }}>Export to PDF</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ResumeBuilder;
