// src/pages/PreviewPage.jsx
import React, { useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api";
import "../styles/PreviewPage.css";

export default function PreviewPage() {
  const { state } = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    // If user navigates directly to /preview without state, send back to builder
    if (!state || !state.previewHtml) {
      navigate("/", { replace: true });
    }
  }, [state, navigate]);

  if (!state || !state.previewHtml) return null;

  const { form, summary, previewHtml } = state;

  const exportPdf = async () => {
    try {
      // payload to backend - include structured data for formatting
      const payload = {
        name: form.name,
        role: form.role,
        email: form.emailId,
        phone: form.phoneNo,
        address: "", // optional
        summary,
        skills: form.skills || [],
        education: form.education || [],
        experience: form.experience || [],
        projects: form.projects || [],
        certificates: form.certificates || [],
      };

      const res = await api.post("/pdf/export", payload, { responseType: "blob" });

      const blob = new Blob([res.data], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${form.name || "resume"}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Export PDF error:", err?.response?.data ?? err?.message);
      alert("Failed to export PDF. Check backend endpoint or network.");
    }
  };

  return (
    <div className="preview-page-root">
      <div className="preview-header">
        <button className="btn-secondary" onClick={() => navigate(-1)}>‚Üê Back</button>
        <div>
          <button className="btn-primary" onClick={exportPdf}>Export PDF</button>
        </div>
      </div>

      <div className="a4-wrapper">
        {/* A4 area (210mm x 297mm) with 32px padding inside, previewHtml as content */}
        <div className="a4-page" dangerouslySetInnerHTML={{ __html: previewHtml }} />
      </div>
    </div>
  );
}
