import React,{useState} from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

export default function PreviewPage() {
  const navigate = useNavigate();
  const { state } = useLocation();
  const [pdfLoading, setPdfloading] = useState(false)

  if (!state) {
    return (
      <div style={{ padding: 40 }}>
        <h2>No data received</h2>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    );
  }

  const { form, gensummary } = state;

  // ----------------- Export PDF -----------------
const exportPDF = async () => {
  try {
    setPdfloading(true)
    const response = await axios.post(
      "https://ai-resume-builder-backend-u4v2.onrender.com/pdf/export-base64",
      { form, gensummary },
      { responseType: "json" }
    );

    if (!response.data || !response.data.ok) {
      console.error("Server responded but didn't return PDF:", response.data);
      alert("Failed to generate PDF on server. Check logs.");
      return;
    }

    const { pdfBase64, filename, size } = response.data;

    if (!pdfBase64) {
      console.error("Missing pdfBase64 in response:", response.data);
      alert("No PDF returned from server.");
      return;
    }

    // Convert base64 to binary
    const byteChars = atob(pdfBase64);
    const byteNumbers = new Array(byteChars.length);
    for (let i = 0; i < byteChars.length; i++) {
      byteNumbers[i] = byteChars.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    const blob = new Blob([byteArray], { type: "application/pdf" });

    // Optional: quick client-side check of PDF signature
    const header = Array.from(new Uint8Array(blob.slice(0, 5))).map(b => b.toString(16).padStart(2, "0")).join("");
    console.log("Client PDF header (hex):", header); // should contain '25504446' (i.e. %PDF)
    console.log("Server reported size:", size, "blob size:", blob.size);

    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = filename || `${form.name || "resume"}.pdf`;
    a.click();
    setPdfloading(false)
    window.URL.revokeObjectURL(url);

  } catch (err) {
    setPdfloading(false)
    console.error("Export error:", err);
    alert("Failed to export PDF â€” check console for details.");
  }
};




  return (
    <div
      style={{
        width: "794px",
        minHeight: "1123px",
        margin: "20px auto",
        padding: "40px",
        background: "white",
        boxShadow: "0 0 10px rgba(0,0,0,0.15)",
        fontFamily: "Arial",
      }}
    >
      {/* ----------------- Buttons ----------------- */}
      <div className="flex justify-between mb-6 items-center">
        <button
          onClick={() => navigate(-1)}
          className="px-4 py-2 bg-gray-600 rounded-lg hover:bg-gray-300"
        >
          â¬… Back
        </button>

        <button
          onClick={exportPDF}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
        >
          {pdfLoading ? "Exporting PDF..." : "ðŸ“„ Export PDF"}
        </button>
      </div>


      {/* ===================== HEADER ===================== */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <h1 style={{ margin: 0, fontWeight:"600", fontSize:"18px" }}>{form.name}</h1>
        <h3 style={{ margin: 0, fontWeight: 500 }}>{form.role}</h3>

        <p style={{ marginTop: "10px", fontSize: "14px" }}>
          {form.emailId} | {form.phoneNo} | {form.linkedIn} | {form.portfolioLink}
        </p>
      </div>

      {/* ===================== SUMMARY ===================== */}
      <h2 style={{fontWeight: "600", color:"#111111", fontSize:"16px"}}>SUMMARY</h2>
      <hr style={{border: "1px solid", marginBottom:"10px"}}/>
      <p>{gensummary}</p>

      {/* ===================== EDUCATION ===================== */}
      <h2 style={{fontWeight: "600",color:"#111111", fontSize:"16px"}}>EDUCATION</h2>
      <hr style={{border: "1px solid", marginBottom:"10px"}}/>
      {form.education?.map((edu, index) => (
        <div key={index} style={{ marginBottom: "12px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span style={{fontWeight: "600", color:"#2B2B2B"}}>{edu.institute}</span>
            <span>{edu.startYear} - {edu.endYear}</span>
          </div>
          <div style={{ display: "flex", gap: "10px", marginTop: "4px" }}>
            <span>{edu.eduType}</span>
            <span>{edu.department}</span>
            <span>{edu.score}</span>
          </div>
        </div>
      ))}

      {/* ===================== SKILLS ===================== */}
      {form.skills?.length > 0 && (
        <>
          <h2 style={{fontWeight: "600",color:"#2E2E2E", fontSize:"16px"}}>Skills</h2>
          <hr style={{border: "1px solid", marginBottom:"10px"}}/>
          <div style={{ display: "flex", flexWrap: "wrap", gap: "8px", marginBottom: "20px" }}>
            {form.skills.map((skill, idx) => (
              <span
                key={idx}
                style={{
                  fontSize: "14px"
                }}
              >
                {idx===form.skills.length-1?`${skill}`: `${skill}, `}
              </span>
            ))}
          </div>
        </>
      )}

      {/* ===================== EXPERIENCE ===================== */}
      <h2 style={{fontWeight: "600",color:"#2E2E2E", fontSize:"16px"}}>Experience</h2>
      <hr style={{border: "1px solid", marginBottom:"10px"}}/>
      {form.experience?.map((exp, index) => (
        <div key={index} style={{ marginBottom: "12px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span>{exp.role}</span>
            <span>{exp.duration} Year</span>
          </div>
          <div>{exp.company}</div>
          {exp.activities && <p>{exp.activities}</p>}
        </div>
      ))}

      {/* ===================== PROJECTS ===================== */}
      <h2 style={{fontWeight: "600",color:"#2E2E2E", fontSize:"16px"}}>Projects</h2>
      <hr style={{border: "1px solid", marginBottom:"10px"}}/>
      {form.projects?.map((p, index) => (
        <div key={index} style={{ marginBottom: "14px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span>{p.name}</span>
            {p.link && <a href={p.link} style={{color: "blue"}}>{p.link}</a>}
          </div>
          <p>{p.description}</p>

          {p.keyPoints?.filter(Boolean).length > 0 && (
            <ul style={{ marginLeft: "20px" }}>
              {p.keyPoints.map((kp, i) => kp && <li key={i}>{kp}</li>)}
            </ul>
          )}

          {p.technologies && (
            <p>
              <strong style={{fontWeight:"540"}}>Tech Used:</strong> {p.technologies}
            </p>
          )}
        </div>
      ))}

      {/* ===================== CERTIFICATES ===================== */}
      <h2 style={{fontWeight: "600",color:"#2E2E2E", fontSize:"16px"}}>Certificates</h2>
      <hr style={{border: "1px solid", marginBottom:"10px"}}/>
      {form.certificates?.map((c, index) => (
        <div key={index} style={{ marginBottom: "10px" }}>
          <div style={{display:"flex", alignItems: "center", justifyContent:"space-between"}}>
            <strong>{c.title}</strong>
            <span>{c.issuedOn}</span>
          </div>
          
          <p>Issued By: {c.issuedBy}</p>
          {c.credential && <p>Credential: {c.credential}</p>}
        </div>
      ))}
    </div>
  );
}
