import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api";
import "../styles/PreviewPage.css";

export default function PreviewPage() {
  const { state } = useLocation();
  const navigate = useNavigate();

  // if user navigates directly, redirect to builder
  if (!state || !state.previewHtml) {
    navigate("/", { replace: true });
    return null;
  }

  const { form, summary, previewHtml } = state;

  async function exportPdf() {
    try {
      let res;

      const payload = {
        name: form.name,
        email: form.emailId,
        summary,
        skills: form.skills,

        // ⬇ NEW FIELDS ADDED HERE:
        education: form.education,
        experience: form.experience,
        projects: form.projects,
        certificates: form.certificates,

        html: previewHtml
      };

      try {
        res = await api.post("/pdf/export", payload, { responseType: "blob" });
      } catch (err) {
        res = await api.post("/api/pdf", { html: previewHtml }, { responseType: "blob" });
      }

      const blob = new Blob([res.data], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);

      const a = document.createElement("a");
      a.href = url;
      a.download = `${form.name || "resume"}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);

    } catch (err) {
      console.error("PDF Export Error: ", err);
      alert("Failed to export PDF.");
    }
  }

  return (
    <div className="preview-page-wrapper">
      <div className="preview-header">
        <button className="btn-secondary" onClick={() => navigate(-1)}>
          ← Back
        </button>

        <button className="btn-primary" onClick={exportPdf}>
          Export PDF
        </button>
      </div>

      {/* The generated preview HTML (already includes all new sections) */}
      <div
        className="preview-content-box"
        dangerouslySetInnerHTML={{ __html: previewHtml }}
      />
    </div>
  );
}
