import React,{useState} from "react";
import { useLocation, useNavigate } from "react-router-dom";
import axios from "axios";

export default function PreviewPage() {
  const navigate = useNavigate();
  const { state } = useLocation();

  if (!state) {
    return (
      <div style={{ padding: 40 }}>
        <h2>No data received</h2>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    );
  }

  const { form, gensummary } = state;

  // ----------------- Export PDF -----------------
  const exportPDF = async () => {
  try {
    const response = await axios.post(
      "https://ai-resume-builder-backend-u4v2.onrender.com/pdf/export",
      { form, gensummary },
      {
        responseType: "arraybuffer",   // IMPORTANT FIX
      }
    );

    const blob = new Blob([response.data], { type: "application/pdf" });

    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `${form.name || "resume"}.pdf`;
    a.click();

    window.URL.revokeObjectURL(url);

  } catch (error) {
    console.error("‚ùå PDF DOWNLOAD ERROR:", error);
    alert("Failed to export PDF");
  }
};




  return (
    <div
      style={{
        width: "794px",
        minHeight: "1123px",
        margin: "20px auto",
        padding: "40px",
        background: "white",
        boxShadow: "0 0 10px rgba(0,0,0,0.15)",
        fontFamily: "Arial",
      }}
    >
      {/* ----------------- Buttons ----------------- */}
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
        <button onClick={() => navigate(-1)} className="btn-secondary">
          ‚¨Ö Back
        </button>
        <button onClick={exportPDF} className="btn-primary">
          üìÑ Export PDF
        </button>
      </div>

      {/* ===================== HEADER ===================== */}
      <div style={{ textAlign: "center", marginBottom: "20px" }}>
        <h1 style={{ margin: 0 }}>{form.name}</h1>
        <h3 style={{ margin: 0, fontWeight: 500 }}>{form.role}</h3>

        <p style={{ marginTop: "10px", fontSize: "14px" }}>
          {form.emailId} | {form.phoneNo} | {form.linkedIn} | {form.portfolioLink}
        </p>
      </div>

      {/* ===================== SUMMARY ===================== */}
      <h2>Summary</h2>
      <hr />
      <p>{gensummary}</p>

      {/* ===================== EDUCATION ===================== */}
      <h2>Education</h2>
      <hr />
      {form.education?.map((edu, index) => (
        <div key={index} style={{ marginBottom: "12px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span>{edu.institute}</span>
            <span>{edu.startYear} - {edu.endYear}</span>
          </div>
          <div style={{ display: "flex", gap: "10px", marginTop: "4px" }}>
            <span>{edu.eduType}</span>
            <span>{edu.department}</span>
            <span>{edu.score}</span>
          </div>
        </div>
      ))}

      {/* ===================== SKILLS ===================== */}
      {form.skills?.length > 0 && (
        <>
          <h2>Skills</h2>
          <hr />
          <div style={{ display: "flex", flexWrap: "wrap", gap: "8px", marginBottom: "20px" }}>
            {form.skills.map((skill, idx) => (
              <span
                key={idx}
                style={{
                  padding: "6px 12px",
                  border: "1px solid #000",
                  borderRadius: "6px",
                  fontSize: "14px"
                }}
              >
                {skill}
              </span>
            ))}
          </div>
        </>
      )}

      {/* ===================== EXPERIENCE ===================== */}
      <h2>Experience</h2>
      <hr />
      {form.experience?.map((exp, index) => (
        <div key={index} style={{ marginBottom: "12px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span>{exp.role}</span>
            <span>{exp.duration}</span>
          </div>
          <div>{exp.company}</div>
          {exp.activities && <p>{exp.activities}</p>}
        </div>
      ))}

      {/* ===================== PROJECTS ===================== */}
      <h2>Projects</h2>
      <hr />
      {form.projects?.map((p, index) => (
        <div key={index} style={{ marginBottom: "14px" }}>
          <div style={{ display: "flex", justifyContent: "space-between", fontWeight: 600 }}>
            <span>{p.name}</span>
            {p.link && <a href={p.link}>{p.link}</a>}
          </div>
          <p>{p.description}</p>

          {p.keyPoints?.filter(Boolean).length > 0 && (
            <ul style={{ marginLeft: "20px" }}>
              {p.keyPoints.map((kp, i) => kp && <li key={i}>{kp}</li>)}
            </ul>
          )}

          {p.technologies && (
            <p>
              <strong>Tech Used:</strong> {p.technologies}
            </p>
          )}
        </div>
      ))}

      {/* ===================== CERTIFICATES ===================== */}
      <h2>Certificates</h2>
      <hr />
      {form.certificates?.map((c, index) => (
        <div key={index} style={{ marginBottom: "10px" }}>
          <strong>{c.title}</strong>
          <p>{c.issuedBy}</p>
          {c.credential && <p>{c.credential}</p>}
        </div>
      ))}
    </div>
  );
}
