// src/pages/PreviewPage.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api";
import "../styles/PreviewPage.css";

export default function PreviewPage() {
  const { state } = useLocation();
  const navigate = useNavigate();

  // redirect if no state (direct access)
  if (!state || !state.previewHtml) {
    navigate("/", { replace: true });
    return null;
  }

  const { form, summary, previewHtml } = state;

  // Export PDF by calling backend /pdf/export which streams PDF (A4)
  const exportPdf = async () => {
    try {
      // POST body contains full form and summary — backend will render
      const res = await api.post(
        "/pdf/export",
        { form, summary },
        { responseType: "blob" }
      );

      const blob = new Blob([res.data], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${form.name || "resume"}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Export PDF error:", err);
      alert("Failed to export PDF. Check backend endpoint or network.");
    }
  };

  return (
    <div className="preview-page-wrapper">
      <div className="preview-header">
        <button className="btn-secondary" onClick={() => navigate(-1)}>← Back</button>
        <div>
          <button className="btn-primary" onClick={exportPdf}>Export PDF</button>
        </div>
      </div>

      <div className="preview-a4">
        {/* the previewHtml was built to match PDF layout */}
        <div className="preview-content" dangerouslySetInnerHTML={{ __html: previewHtml }} />
      </div>
    </div>
  );
}
