import React, { useState } from "react";
import axios from "axios";

const ResumeBuilder = () => {
  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,
    education: [{ institute: "", city: "", year: "", grade: "" }],
    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [{ name: "", description: "", technologies: "", keyPoints: "", link: "" }],
    certificates: [{ title: "", issuedBy: "", issuedOn: "", credential: "" }],
    skills: [""],
  });

  const [summary, setSummary] = useState("");
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (section, index, field, value) => {
    if (Array.isArray(form[section])) {
      const updated = [...form[section]];
      updated[index][field] = value;
      setForm({ ...form, [section]: updated });
    } else {
      setForm({ ...form, [section]: value });
    }
  };

  const addField = (section, structure) => {
    setForm({ ...form, [section]: [...form[section], structure] });
  };

  const removeField = (section, index) => {
    const updated = [...form[section]];
    updated.splice(index, 1);
    setForm({ ...form, [section]: updated });
  };

  const generateSummary = async () => {
    try {
      setLoading(true);
      const prompt = `
      Write a professional resume summary for a ${form.role} named ${form.name}
      with ${form.totalExperienceYear} year of experience. Highlight skills in ${form.skills.join(",")},
      focusing on achievements and impact. Keep it under 100 words.
    `;
      const response = await axios.post("https://ai-resume-builder-backend-u4v2.onrender.com/ai/generate", { prompt });
      setSummary(response.data.summary);
    } catch (error) {
      console.error("AI Summary Error:", error);
      alert("Error generating summary.");
    } finally {
      setLoading(false);
    }
  };

  const clean = (text) => text && text.trim() !== "" ? text : null;

  const buildPreview = () => {
    return `
      <div style="font-family: Inter, sans-serif; padding: 20px; line-height: 1.6;">
        <h1 style="font-size: 24px; font-weight: bold;">${clean(form.name) || ""}</h1>
        <p style="font-size: 16px; color: #555;">${clean(form.role) || ""}</p>
        <p style="font-size: 14px; color: #555;">
          ${clean(form.emailId) ? `üìß ${form.emailId}<br/>` : ""}
          ${clean(form.phoneNo) ? `üìû ${form.phoneNo}<br/>` : ""}
          ${clean(form.linkedIn) ? `üîó ${form.linkedIn}<br/>` : ""}
          ${clean(form.portfolioLink) ? `üåê ${form.portfolioLink}` : ""}
        </p>
        ${form.totalExperienceYear > 0 ? `<p><b>Total Experience:</b> ${form.totalExperienceYear} Year(s)</p>` : ""}

        ${summary ? `<h3>Professional Summary</h3><p>${summary}</p>` : ""}

        ${form.education.some(e => e.institute) ? `
        <h3>Education</h3><ul>${form.education.map(e =>
          e.institute ? `<li><b>${e.institute}</b>, ${e.city} (${e.year}) ‚Äî ${e.grade}</li>` : ""
        ).join("")}</ul>` : ""}

        ${form.experience.some(exp => exp.company) ? `
        <h3>Experience</h3><ul>${form.experience.map(exp =>
          exp.company ? `<li><b>${exp.role}</b> @ ${exp.company} (${exp.duration})<br/>${exp.activities}</li>` : ""
        ).join("")}</ul>` : ""}

        ${form.projects.some(p => p.name) ? `
        <h3>Projects</h3><ul>${form.projects.map(p =>
          p.name ? `<li><b>${p.name}</b> ‚Äî ${p.description}<br/><i>${p.technologies}</i><br/>
          ${p.keyPoints ? `‚Ä¢ ${p.keyPoints}` : ""}<br/>
          ${p.link ? `<a href="${p.link}">${p.link}</a>` : ""}
          </li>` : ""
        ).join("")}</ul>` : ""}

        ${form.certificates.some(c => c.title) ? `
        <h3>Certificates</h3><ul>${form.certificates.map(c =>
          c.title ? `<li><b>${c.title}</b> ‚Äî ${c.issuedBy} (${c.issuedOn}) ${c.credential ? `<br/>Credential: ${c.credential}` : ""}</li>` : ""
        ).join("")}</ul>` : ""}

        ${form.skills.some(s => s && s.trim() !== "") ? `<h3>Skills</h3><p>${form.skills.filter(s => s).join(", ")}</p>` : ""}
      </div>
    `;
  };

  const handlePreview = async () => {
    await generateSummary();
    const html = buildPreview();
    setPreview(html);
  };

  const exportPDF = async () => {
    try {
      const response = await axios.post("https://ai-resume-builder-backend-u4v2.onrender.com/api/pdf", { html: preview }, { responseType: "blob" });
      const blob = new Blob([response.data], { type: "application/pdf" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = `${form.name || "resume"}.pdf`;
      link.click();
    } catch (error) {
      console.error("PDF export error:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <div className="max-w-5xl mx-auto bg-white shadow-lg rounded-xl p-8">
        <h2 className="text-3xl font-bold mb-8 text-center text-blue-600">AI Resume Builder</h2>

        {/* --- Basic Info --- */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <input className="border p-2 rounded" placeholder="Full Name" value={form.name} onChange={(e) => handleChange("name", null, null, e.target.value)} />
          <input className="border p-2 rounded" placeholder="Role / Title" value={form.role} onChange={(e) => handleChange("role", null, null, e.target.value)} />
          <input className="border p-2 rounded" placeholder="Email ID" value={form.emailId} onChange={(e) => handleChange("emailId", null, null, e.target.value)} />
          <input className="border p-2 rounded" placeholder="Phone Number" value={form.phoneNo} onChange={(e) => handleChange("phoneNo", null, null, e.target.value)} />
          <input className="border p-2 rounded" placeholder="LinkedIn URL" value={form.linkedIn} onChange={(e) => handleChange("linkedIn", null, null, e.target.value)} />
          <input className="border p-2 rounded" placeholder="Portfolio Link" value={form.portfolioLink} onChange={(e) => handleChange("portfolioLink", null, null, e.target.value)} />
          <input className="border p-2 rounded" type="number" placeholder="Total Experience (in Years)" value={form.totalExperienceYear} onChange={(e) => handleChange("totalExperienceYear", null, null, Number(e.target.value))} />
        </div>

        {/* --- Dynamic Sections --- */}

        {/* Education */}
        <Section title="Education">
          {form.education.map((edu, i) => (
            <div key={i} className="grid grid-cols-1 md:grid-cols-4 gap-2 mb-2">
              <input className="border p-2 rounded" placeholder="Institute" value={edu.institute} onChange={(e) => handleChange("education", i, "institute", e.target.value)} />
              <input className="border p-2 rounded" placeholder="City" value={edu.city} onChange={(e) => handleChange("education", i, "city", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Year" value={edu.year} onChange={(e) => handleChange("education", i, "year", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Grade" value={edu.grade} onChange={(e) => handleChange("education", i, "grade", e.target.value)} />
              <button onClick={() => removeField("education", i)} className="text-red-500 text-sm hover:underline">Remove</button>
            </div>
          ))}
          <AddButton label="Add Education" onClick={() => addField("education", { institute: "", city: "", year: "", grade: "" })} />
        </Section>

        {/* Experience */}
        <Section title="Experience">
          {form.experience.map((exp, i) => (
            <div key={i} className="grid grid-cols-1 md:grid-cols-4 gap-2 mb-2">
              <input className="border p-2 rounded" placeholder="Role" value={exp.role} onChange={(e) => handleChange("experience", i, "role", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Company" value={exp.company} onChange={(e) => handleChange("experience", i, "company", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Duration" value={exp.duration} onChange={(e) => handleChange("experience", i, "duration", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Activities" value={exp.activities} onChange={(e) => handleChange("experience", i, "activities", e.target.value)} />
              <button onClick={() => removeField("experience", i)} className="text-red-500 text-sm hover:underline">Remove</button>
            </div>
          ))}
          <AddButton label="Add Experience" onClick={() => addField("experience", { role: "", company: "", duration: "", activities: "" })} />
        </Section>

        {/* Projects */}
        <Section title="Projects">
          {form.projects.map((proj, i) => (
            <div key={i} className="grid grid-cols-1 md:grid-cols-5 gap-2 mb-2">
              <input className="border p-2 rounded" placeholder="Project Name" value={proj.name} onChange={(e) => handleChange("projects", i, "name", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Description" value={proj.description} onChange={(e) => handleChange("projects", i, "description", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Technologies" value={proj.technologies} onChange={(e) => handleChange("projects", i, "technologies", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Key Points" value={proj.keyPoints} onChange={(e) => handleChange("projects", i, "keyPoints", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Link" value={proj.link} onChange={(e) => handleChange("projects", i, "link", e.target.value)} />
              <button onClick={() => removeField("projects", i)} className="text-red-500 text-sm hover:underline">Remove</button>
            </div>
          ))}
          <AddButton label="Add Project" onClick={() => addField("projects", { name: "", description: "", technologies: "", keyPoints: "", link: "" })} />
        </Section>

        {/* Certificates */}
        <Section title="Certificates">
          {form.certificates.map((cert, i) => (
            <div key={i} className="grid grid-cols-1 md:grid-cols-5 gap-2 mb-2">
              <input className="border p-2 rounded" placeholder="Title" value={cert.title} onChange={(e) => handleChange("certificates", i, "title", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Issued By" value={cert.issuedBy} onChange={(e) => handleChange("certificates", i, "issuedBy", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Issued On" value={cert.issuedOn} onChange={(e) => handleChange("certificates", i, "issuedOn", e.target.value)} />
              <input className="border p-2 rounded" placeholder="Credential ID" value={cert.credential} onChange={(e) => handleChange("certificates", i, "credential", e.target.value)} />
              <button onClick={() => removeField("certificates", i)} className="text-red-500 text-sm hover:underline">Remove</button>
            </div>
          ))}
          <AddButton label="Add Certificate" onClick={() => addField("certificates", { title: "", issuedBy: "", issuedOn: "", credential: "" })} />
        </Section>

        {/* Skills */}
        <Section title="Skills">
          {form.skills.map((skill, i) => (
            <div key={i} className="flex gap-2 mb-2">
              <input className="border p-2 rounded w-full" placeholder="Skill" value={skill} onChange={(e) => handleChange("skills", i, null, e.target.value)} />
              <button onClick={() => removeField("skills", i)} className="text-red-500 text-sm hover:underline">Remove</button>
            </div>
          ))}
          <AddButton label="Add Skill" onClick={() => addField("skills", "")} />
        </Section>

        {/* Buttons */}
        <div className="flex justify-center gap-4 mt-8">
          <button onClick={handlePreview} disabled={loading} className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
            {loading ? "Generating..." : "Preview Resume"}
          </button>
        </div>

        {/* Preview */}
        {preview && (
          <div className="mt-8">
            <h3 className="text-2xl font-semibold mb-4 text-gray-800">Preview</h3>
            <div className="border rounded-lg p-4 bg-gray-50" dangerouslySetInnerHTML={{ __html: preview }} />
            <div className="text-center mt-4">
              <button onClick={exportPDF} className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700">
                Export to PDF
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

/* --- Helper Components --- */
const Section = ({ title, children }) => (
  <div className="mb-8">
    <h3 className="text-xl font-semibold mb-3 text-gray-700">{title}</h3>
    {children}
  </div>
);

const AddButton = ({ label, onClick }) => (
  <button onClick={onClick} className="bg-gray-200 hover:bg-gray-300 text-sm px-3 py-1 rounded mb-2">
    ‚ûï {label}
  </button>
);

export default ResumeBuilder;
