import axios from "axios";

// Use environment variable if provided, fallback to localhost
const API_BASE = process.env.REACT_APP_API_BASE || "https://ai-resume-builder-backend-u4v2.onrender.com";

const api = axios.create({
  baseURL: API_BASE,
});

// Attach token automatically to every request if present
api.interceptors.request.use((config) => {
  const token = localStorage.getItem("token");
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export default api;
