// ResumeBuilder.js
import React, { useState } from "react";
import axios from "axios";

const emptyEducation = {
  institute: "",
  city: "",
  startYear: "",
  endYear: "",
  educationType: "", // Masters, Bachelor, etc.
  department: "",
  score: "", // CGPA/Percentage
};

const emptyExperience = {
  role: "",
  years: "",
  company: "",
  description: "",
};

const emptyProject = {
  title: "",
  link: "",
  description: "",
  points: "",
  tech: "",
};

const emptyCertificate = {
  title: "",
  issuedBy: "",
  year: "",
};

export default function ResumeBuilder({ setPreviewData }) {
  const [form, setForm] = useState({
    name: "",
    role: "",
    email: "",
    phone: "",
    summary: "",
    education: [emptyEducation],
    experience: [emptyExperience],
    projects: [emptyProject],
    certificates: [emptyCertificate],
  });

  // -------------------- HANDLE CHANGE --------------------
  const handleChange = (section, index, field, value) => {
    if (!section) {
      setForm({ ...form, [field]: value });
      return;
    }

    const updated = [...form[section]];
    updated[index][field] = value;

    setForm({ ...form, [section]: updated });
  };

  // -------------------- ADD FIELD --------------------
  const addField = (section, template) => {
    setForm({ ...form, [section]: [...form[section], template] });
  };

  // -------------------- PREVIEW --------------------
  const openPreview = () => {
    setPreviewData(form);
  };

  return (
    <div className="p-5 w-full max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-3">Resume Builder</h1>

      {/* BASIC INFO */}
      <input
        className="input"
        placeholder="Full Name"
        onChange={(e) => handleChange(null, null, "name", e.target.value)}
      />
      <input
        className="input"
        placeholder="Role"
        onChange={(e) => handleChange(null, null, "role", e.target.value)}
      />
      <input
        className="input"
        placeholder="Email"
        onChange={(e) => handleChange(null, null, "email", e.target.value)}
      />
      <input
        className="input"
        placeholder="Phone"
        onChange={(e) => handleChange(null, null, "phone", e.target.value)}
      />

      <textarea
        className="textarea"
        placeholder="Summary"
        onChange={(e) => handleChange(null, null, "summary", e.target.value)}
      />

      {/* ---------------- EDUCATION ---------------- */}
      <h2 className="section-title">Education</h2>
      <hr />
      {form.education.map((item, index) => (
        <div key={index} className="box">
          <input
            className="input"
            placeholder="Institute Name"
            onChange={(e) =>
              handleChange("education", index, "institute", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="City"
            onChange={(e) =>
              handleChange("education", index, "city", e.target.value)
            }
          />

          <div className="row">
            <input
              className="input"
              placeholder="Start Year"
              onChange={(e) =>
                handleChange("education", index, "startYear", e.target.value)
              }
            />
            <input
              className="input"
              placeholder="End Year"
              onChange={(e) =>
                handleChange("education", index, "endYear", e.target.value)
              }
            />
          </div>

          <input
            className="input"
            placeholder="Education Type (Masters/Bachelor)"
            onChange={(e) =>
              handleChange("education", index, "educationType", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Department"
            onChange={(e) =>
              handleChange("education", index, "department", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="CGPA/Percentage"
            onChange={(e) =>
              handleChange("education", index, "score", e.target.value)
            }
          />
        </div>
      ))}

      <button
        className="btn"
        onClick={() => addField("education", emptyEducation)}
      >
        + Add Education
      </button>

      {/* ---------------- EXPERIENCE ---------------- */}
      <h2 className="section-title">Experience</h2>
      <hr />
      {form.experience.map((item, index) => (
        <div key={index} className="box">
          <input
            className="input"
            placeholder="Role"
            onChange={(e) =>
              handleChange("experience", index, "role", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Years (ex: 2020-2023)"
            onChange={(e) =>
              handleChange("experience", index, "years", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Company Name"
            onChange={(e) =>
              handleChange("experience", index, "company", e.target.value)
            }
          />
          <textarea
            className="textarea"
            placeholder="Description"
            onChange={(e) =>
              handleChange("experience", index, "description", e.target.value)
            }
          />
        </div>
      ))}

      <button
        className="btn"
        onClick={() => addField("experience", emptyExperience)}
      >
        + Add Experience
      </button>

      {/* ---------------- PROJECTS ---------------- */}
      <h2 className="section-title">Projects</h2>
      <hr />
      {form.projects.map((item, index) => (
        <div key={index} className="box">
          <input
            className="input"
            placeholder="Project Title"
            onChange={(e) =>
              handleChange("projects", index, "title", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Link"
            onChange={(e) =>
              handleChange("projects", index, "link", e.target.value)
            }
          />

          <textarea
            className="textarea"
            placeholder="Description"
            onChange={(e) =>
              handleChange("projects", index, "description", e.target.value)
            }
          />

          <textarea
            className="textarea"
            placeholder="Key Points (comma separated)"
            onChange={(e) =>
              handleChange("projects", index, "points", e.target.value)
            }
          />

          <input
            className="input"
            placeholder="Tech Used"
            onChange={(e) =>
              handleChange("projects", index, "tech", e.target.value)
            }
          />
        </div>
      ))}

      <button
        className="btn"
        onClick={() => addField("projects", emptyProject)}
      >
        + Add Project
      </button>

      {/* ---------------- CERTIFICATES ---------------- */}
      <h2 className="section-title">Certificates</h2>
      <hr />
      {form.certificates.map((item, i) => (
        <div key={i} className="box">
          <input
            className="input"
            placeholder="Certificate Title"
            onChange={(e) =>
              handleChange("certificates", i, "title", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Issued By"
            onChange={(e) =>
              handleChange("certificates", i, "issuedBy", e.target.value)
            }
          />
          <input
            className="input"
            placeholder="Year"
            onChange={(e) =>
              handleChange("certificates", i, "year", e.target.value)
            }
          />
        </div>
      ))}

      <button
        className="btn"
        onClick={() => addField("certificates", emptyCertificate)}
      >
        + Add Certificate
      </button>

      <button className="btn-primary" onClick={openPreview}>
        Open Preview
      </button>
    </div>
  );
}
