// src/components/ResumeBuilder.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api"; // your axios instance
import "../styles/ResumeBuilder.css";

const ResumeBuilder = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,
    summary:"",
    education: [
      { institute: "", city: "", startYear: "", endYear: "", eduType: "", department: "", score: "" }
    ],

    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [{ name: "", description: "", technologies: "", keyPoints: [""], link: "" }],
    certificates: [{ title: "", issuedBy: "", issuedOn: "", credential: "" }],
    skills: [""],
    highestEducation: "",
  });

  const [gensummary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  // scalar setter
  const setScalar = (key, value) => setForm((s) => ({ ...s, [key]: value }));

  // update any array-based field
  const updateArrayField = (section, index, field, value) => {
    const copy = [...form[section]];
    if (field === null) {
      copy[index] = value;
    } else {
      copy[index] = { ...copy[index], [field]: value };
    }
    setForm({ ...form, [section]: copy });
  };

  const addRow = (section, template) => {
    setForm((s) => ({ ...s, [section]: [...s[section], template] }));
  };

  const addProjectKeyPoint = (projectIndex) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints = projects[projectIndex].keyPoints || [""];
    projects[projectIndex].keyPoints.push("");
    setForm({ ...form, projects });
  };

  const updateProjectKeyPoint = (projectIndex, kpIndex, value) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints[kpIndex] = value;
    setForm({ ...form, projects });
  };

  // Build AI prompt
  const buildPrompt = () => {
    const skillsText = form.skills.filter(Boolean).join(", ") || "N/A";
    return `
Write a professional resume summary for a ${form.role || "professional"} named ${form.name || ""} with ${form.totalExperienceYear || 0} year(s) of experience.
Highlight these skills: ${skillsText}.
Keep it concise (<= 100 words), results-focused, and suitable for a recruiter.
`.trim();
  };

  // call backend AI endpoint
  const generateSummary = async () => {
    setLoading(true);
    try {
      const prompt = buildPrompt();
      const res = await api.post("/ai/generate", { prompt });
      // accept several possible shapes
      const aiSummary = res.data?.summary ?? res.data?.result ?? res.data?.data ?? "";
      setSummary(aiSummary);
      return aiSummary;
    } catch (err) {
      console.error("AI generation error:", err?.response?.data ?? err?.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // SAFE preview html generator (matches backend PDF layout)
  const buildPreviewHtml = (resolvedSummary) => {
    const clean = (x) => (x && String(x).trim() ? String(x).trim() : "");

    const eduHtml = (form.education || [])
      .filter((e) => clean(e.institute))
      .map(
        (e) => `
      <div class="edu-item">
        <div class="row-space">
          <strong>${clean(e.institute)}</strong>
          <span>${clean(e.startYear)}${(clean(e.startYear) || clean(e.endYear)) ? " - " : ""}${clean(e.endYear)}</span>
        </div>
        <div class="left-row muted">
          ${clean(e.eduType)}${clean(e.department) ? ` ‚Äî ${clean(e.department)}` : ""}${clean(e.score) ? ` ‚Äî ${clean(e.score)}` : ""}
        </div>
      </div>`
      )
      .join("");

    const expHtml = (form.experience || [])
      .filter((e) => clean(e.role) || clean(e.company))
      .map(
        (e) => `
      <div class="exp-item">
        <div class="row-space">
          <strong>${clean(e.role)}</strong>
          <span>${clean(e.duration)}</span>
        </div>
        <div class="left-row muted">${clean(e.company)}</div>
        ${clean(e.activities) ? `<div class="left-row">${clean(e.activities)}</div>` : ""}
      </div>`
      )
      .join("");

    const projectsHtml = (form.projects || [])
      .filter((p) => clean(p.name))
      .map(
        (p) => `
      <div class="proj-item">
        <div class="row-space">
          <strong>${clean(p.name)}</strong>
          <span>${clean(p.link) ? `<a href="${clean(p.link)}" target="_blank" rel="noreferrer">${clean(p.link)}</a>` : ""}</span>
        </div>
        ${clean(p.description) ? `<div class="left-row muted">${clean(p.description)}</div>` : ""}
        ${
          p.keyPoints?.filter((kp) => clean(kp)).length
            ? `<ul class="kp-list left-row">${p.keyPoints.filter((kp) => clean(kp)).map((kp) => `<li>${kp}</li>`).join("")}</ul>`
            : ""
        }
        ${clean(p.technologies) ? `<div class="left-row muted"><strong>Tech Used:</strong> ${clean(p.technologies)}</div>` : ""}
      </div>`
      )
      .join("");

    const certHtml = (form.certificates || [])
      .filter((c) => clean(c.title))
      .map(
        (c) => `
      <div class="cert-item">
        <strong>${clean(c.title)}</strong>
        <div class="left-row muted">${clean(c.issuedBy) || ""}${clean(c.issuedOn) ? ` ‚Äî ${clean(c.issuedOn)}` : ""}</div>
        ${clean(c.credential) ? `<div class="left-row"><a href="${clean(c.credential)}" target="_blank" rel="noreferrer">${clean(c.credential)}</a></div>` : ""}
      </div>`
      )
      .join("");

    const skillsHtml = form.skills.filter(Boolean).length
      ? `<div class="skills-wrap">${form.skills.filter(Boolean).map((s) => `<span class="skill-pill">${s}</span>`).join(" ")}</div>`
      : "";

    return `
      <div class="preview-root">
        <h1 class="center">${clean(form.name) || ""}</h1>
        <div class="center muted">${clean(form.role) || ""}</div>

        <div class="contact center">
          ${clean(form.emailId) ? `üìß ${clean(form.emailId)} ` : ""}
          ${clean(form.phoneNo) ? ` | üìû ${clean(form.phoneNo)} ` : ""}
          ${clean(form.linkedIn) ? ` | üîó ${clean(form.linkedIn)} ` : ""}
          ${clean(form.portfolioLink) ? ` | üåê ${clean(form.portfolioLink)}` : ""}
        </div>

        ${resolvedSummary ? `<h3>Professional Summary</h3><hr/><div class="section-body">${resolvedSummary}</div>` : ""}

        ${eduHtml ? `<h3>Education</h3><hr/><div class="section-body">${eduHtml}</div>` : ""}
        ${expHtml ? `<h3>Experience</h3><hr/><div class="section-body">${expHtml}</div>` : ""}
        ${projectsHtml ? `<h3>Projects</h3><hr/><div class="section-body">${projectsHtml}</div>` : ""}
        ${certHtml ? `<h3>Certificates</h3><hr/><div class="section-body">${certHtml}</div>` : ""}
        ${skillsHtml ? `<h3>Skills</h3><hr/><div class="section-body">${skillsHtml}</div>` : ""}
      </div>
    `;
  };

  // generic inline handlers for backward compatibility
  const handleChange = (section, index, field, value) => {
    if (index === null && field === null) {
      setForm({ ...form, [section]: value });
      return;
    }
    const updated = [...form[section]];
    updated[index] = { ...updated[index], [field]: value };
    setForm({ ...form, [section]: updated });
  };

  const addField = (section, newItem) => {
    setForm((prev) => ({ ...prev, [section]: [...prev[section], newItem] }));
  };

  // handle preview navigation
  const handlePreview = async () => {
  try {
    // üîî SHOW ALERT BEFORE GENERATING SUMMARY
    setLoading(true);

    
    const previewHtml = buildPreviewHtml(aiSummary);

    navigate("/preview", {
  state: {
    form,
    summary,
    previewHtml
  }
});


  } catch (err) {
    alert("Error generating Summary ‚Äî please check network or API key.");
    console.error(err);
  } finally {
    setLoading(false);
  }
};


  return (
    <div className="rb-root">
      <h2>AI Resume Builder</h2>

      <section className="resume-section">
        <h3>Personal Details</h3>
        <div className="grid-2">
          <input placeholder="Full Name" value={form.name} onChange={(e) => setScalar("name", e.target.value)} />
          <input placeholder="Role / Title" value={form.role} onChange={(e) => setScalar("role", e.target.value)} />
          <input placeholder="Email ID" value={form.emailId} onChange={(e) => setScalar("emailId", e.target.value)} />
          <input placeholder="Phone Number" value={form.phoneNo} onChange={(e) => setScalar("phoneNo", e.target.value)} />
          <input placeholder="LinkedIn URL" value={form.linkedIn} onChange={(e) => setScalar("linkedIn", e.target.value)} />
          <input placeholder="Portfolio Link" value={form.portfolioLink} onChange={(e) => setScalar("portfolioLink", e.target.value)} />
          <input type="number" placeholder="Total Experience (Years)" value={form.totalExperienceYear} onChange={(e) => setScalar("totalExperienceYear", Number(e.target.value))} />
        </div>
      </section>

      <h3 className="section-title">Education</h3>

      <div className="section-box">
        <label className="input-label">Highest Qualification</label>
        <select value={form.highestEducation || ""} onChange={(e) => handleChange("highestEducation", null, null, e.target.value)} className="select-input">
          <option value="">Select Highest Qualification</option>
          <option value="MA">MA</option>
          <option value="MBA">MBA</option>
          <option value="MCA">MCA</option>
          <option value="MSc">MSc</option>
          <option value="B.Tech">B.Tech</option>
          <option value="B.Com">B.Com</option>
          <option value="B.Sc">B.Sc</option>
          <option value="Diploma">Diploma</option>
          <option value="12th">Higher Secondary (12th)</option>
          <option value="10th">SSLC / 10th</option>
        </select>
      </div>

      {form.education.map((edu, idx) => (
        <div className="section-box" key={idx}>
          <div className="row-flex">
            <input placeholder="Institute Name" value={edu.institute} onChange={(e) => handleChange("education", idx, "institute", e.target.value)} />
            <input placeholder="Start Year" value={edu.startYear} onChange={(e) => handleChange("education", idx, "startYear", e.target.value)} />
            <input placeholder="End Year" value={edu.endYear} onChange={(e) => handleChange("education", idx, "endYear", e.target.value)} />
          </div>

          <div className="row-flex">
            <select value={edu.eduType} onChange={(e) => handleChange("education", idx, "eduType", e.target.value)}>
              <option value="">Education Type</option>
              <option value="Masters">Masters</option>
              <option value="Bachelor">Bachelor</option>
              <option value="Diploma">Diploma</option>
              <option value="Higher Secondary">Higher Secondary</option>
              <option value="SSLC">SSLC / 10th</option>
            </select>

            <input placeholder="Department / Stream" value={edu.department} onChange={(e) => handleChange("education", idx, "department", e.target.value)} />
            <input placeholder="CGPA / Percentage" value={edu.score} onChange={(e) => handleChange("education", idx, "score", e.target.value)} />
          </div>
        </div>
      ))}

      <button className="add-btn" onClick={() => addField("education", { institute: "", city: "", startYear: "", endYear: "", eduType: "", department: "", score: "" })}>+ Add Education Entry</button>

      <section className="resume-section">
        <h3>Experience</h3>
        {form.experience.map((ex, i) => (
          <div key={i} className="section-box">
            <div className="grid-2">
              <input placeholder="Role" value={ex.role} onChange={(e) => updateArrayField("experience", i, "role", e.target.value)} />
              <input placeholder="Company" value={ex.company} onChange={(e) => updateArrayField("experience", i, "company", e.target.value)} />
              <input placeholder="Duration" value={ex.duration} onChange={(e) => updateArrayField("experience", i, "duration", e.target.value)} />
              <input placeholder="Activities" value={ex.activities} onChange={(e) => updateArrayField("experience", i, "activities", e.target.value)} />
            </div>
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("experience", { role: "", company: "", duration: "", activities: "" })}>+ Add Experience</button>
      </section>

      <section className="resume-section">
        <h3>Projects</h3>
        {form.projects.map((p, i) => (
          <div key={i} className="project-block">
            <input placeholder="Project Name" value={p.name} onChange={(e) => updateArrayField("projects", i, "name", e.target.value)} />
            <input placeholder="Short Description" value={p.description} onChange={(e) => updateArrayField("projects", i, "description", e.target.value)} />
            <input placeholder="Technologies (comma separated)" value={p.technologies} onChange={(e) => updateArrayField("projects", i, "technologies", e.target.value)} />
            <input placeholder="Project Link" value={p.link} onChange={(e) => updateArrayField("projects", i, "link", e.target.value)} />

            <div className="keypoints">
              <h4>Key Points</h4>
              {p.keyPoints.map((kp, kpi) => (
                <input key={kpi} placeholder="Key point" value={kp} onChange={(e) => updateProjectKeyPoint(i, kpi, e.target.value)} />
              ))}
              <button className="btn-mini" onClick={() => addProjectKeyPoint(i)}>+ Add Key Point</button>
            </div>
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("projects", { name: "", description: "", technologies: "", keyPoints: [""], link: "" })}>+ Add Project</button>
      </section>

      <section className="resume-section">
        <h3>Certificates</h3>
        {form.certificates.map((c, i) => (
          <div key={i} className="section-box">
            <div className="grid-2">
              <input placeholder="Title" value={c.title} onChange={(e) => updateArrayField("certificates", i, "title", e.target.value)} />
              <input placeholder="Issued By" value={c.issuedBy} onChange={(e) => updateArrayField("certificates", i, "issuedBy", e.target.value)} />
              <input placeholder="Issued On" value={c.issuedOn} onChange={(e) => updateArrayField("certificates", i, "issuedOn", e.target.value)} />
              <input placeholder="Credential Link / ID" value={c.credential} onChange={(e) => updateArrayField("certificates", i, "credential", e.target.value)} />
            </div>
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("certificates", { title: "", issuedBy: "", issuedOn: "", credential: "" })}>+ Add Certificate</button>
      </section>

      <section className="resume-section">
        <h3>Skills</h3>
        {form.skills.map((s, i) => (
          <input key={i} placeholder="Skill" value={s} onChange={(e) => updateArrayField("skills", i, null, e.target.value)} />
        ))}
        <button className="btn-secondary" onClick={() => addRow("skills", "")}>+ Add Skill</button>
      </section>

      // Inside ResumeBuilder component, after all input sections

    <div className="center-box">
      <button
        className="btn-primary"
        onClick={async () => {
          try {
            setLoading(true);
            const aiSummary = await generateSummary();
            // Remove all asterisks from AI-generated summary
            setSummary(aiSummary.replace(/\*/g, ""));
          } catch (err) {
            alert("Error generating summary ‚Äî please check network or API key.");
          } finally {
            setLoading(false);
          }
        }}
        disabled={loading}
      >
        {summary ? "Generate Another" : "Generate Summary"}
      </button>

      {summary && (
        <div
          className="generated-summary mt-3"
          style={{
            padding: "10px",
            border: "1px solid #ddd",
            borderRadius: "6px",
            background: "#f9f9f9",
          }}
        >
          {summary}
        </div>
      )}
    </div>

    <div className="center-box">
      <button
        className="btn-primary"
        onClick={() => {
          if (!summary) {
            alert("Please generate a summary first!");
            return;
          }
          const previewHtml = buildPreviewHtml(summary);
          navigate("/preview", { state: { form, summary, previewHtml } });
        }}
        disabled={!summary}
      >
        Preview Resume
      </button>
      </div>

    </div>
  );
};

export default ResumeBuilder;
