import React, { useState } from "react";
import PreviewPage from "./PreviewPage";

export default function ResumeBuilder() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    summary: "",
    skills: [],
    education: [
      {
        institute: "",
        startYear: "",
        endYear: "",
        type: "",
        department: "",
        grade: "",
      },
    ],
    experience: [
      {
        role: "",
        company: "",
        duration: "",
        activities: "",
      },
    ],
    projects: [
      {
        name: "",
        link: "",
        description: "",
        keyPoints: [],
        technologies: "",
      },
    ],
    certificates: [
      {
        title: "",
        issuedBy: "",
        issuedOn: "",
        credential: "",
      },
    ],
  });

  const [showPreview, setShowPreview] = useState(false);

  // -------------------- HANDLE CHANGE --------------------
  const handleChange = (section, index, field, value) => {
    setForm((prev) => {
      const updated = { ...prev };
      updated[section][index][field] = value;
      return updated;
    });
  };

  // -------------------- ADD FIELD --------------------
  const addField = (section, emptyObj) => {
    setForm((prev) => ({
      ...prev,
      [section]: [...prev[section], emptyObj],
    }));
  };

  const exportPDF = async () => {
    const res = await fetch("http://localhost:5000/pdf/export", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });

    const blob = await res.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "resume.pdf";
    a.click();
  };

  return (
    <div>
      <h1>Resume Builder</h1>

      {/* ---------------- BASIC FIELDS ---------------- */}
      <input
        placeholder="Full Name"
        value={form.name}
        onChange={(e) => setForm({ ...form, name: e.target.value })}
      />
      <input
        placeholder="Email"
        value={form.email}
        onChange={(e) => setForm({ ...form, email: e.target.value })}
      />
      <textarea
        placeholder="Summary"
        value={form.summary}
        onChange={(e) => setForm({ ...form, summary: e.target.value })}
      />

      {/* ---------------- EDUCATION SECTION ---------------- */}
      <h3>Education</h3>
      {form.education.map((edu, i) => (
        <div key={i}>
          <input
            placeholder="Institute Name"
            value={edu.institute}
            onChange={(e) =>
              handleChange("education", i, "institute", e.target.value)
            }
          />

          <input
            placeholder="Start Year"
            value={edu.startYear}
            onChange={(e) =>
              handleChange("education", i, "startYear", e.target.value)
            }
          />

          <input
            placeholder="End Year"
            value={edu.endYear}
            onChange={(e) =>
              handleChange("education", i, "endYear", e.target.value)
            }
          />

          <input
            placeholder="Education Type (Masters/Bachelor)"
            value={edu.type}
            onChange={(e) =>
              handleChange("education", i, "type", e.target.value)
            }
          />

          <input
            placeholder="Department"
            value={edu.department}
            onChange={(e) =>
              handleChange("education", i, "department", e.target.value)
            }
          />

          <input
            placeholder="CGPA / Percentage"
            value={edu.grade}
            onChange={(e) =>
              handleChange("education", i, "grade", e.target.value)
            }
          />
        </div>
      ))}

      <button
        onClick={() =>
          addField("education", {
            institute: "",
            startYear: "",
            endYear: "",
            type: "",
            department: "",
            grade: "",
          })
        }
      >
        + Add Education
      </button>

      {/* ---------------- EXPERIENCE SECTION ---------------- */}
      <h3>Experience</h3>
      {form.experience.map((exp, i) => (
        <div key={i}>
          <input
            placeholder="Role"
            value={exp.role}
            onChange={(e) => handleChange("experience", i, "role", e.target.value)}
          />

          <input
            placeholder="Company"
            value={exp.company}
            onChange={(e) =>
              handleChange("experience", i, "company", e.target.value)
            }
          />

          <input
            placeholder="Duration"
            value={exp.duration}
            onChange={(e) =>
              handleChange("experience", i, "duration", e.target.value)
            }
          />

          <textarea
            placeholder="Description"
            value={exp.activities}
            onChange={(e) =>
              handleChange("experience", i, "activities", e.target.value)
            }
          />
        </div>
      ))}

      <button
        onClick={() =>
          addField("experience", {
            role: "",
            company: "",
            duration: "",
            activities: "",
          })
        }
      >
        + Add Experience
      </button>

      {/* ---------------- PREVIEW + EXPORT BUTTONS ---------------- */}
      <button onClick={() => setShowPreview(true)}>Preview</button>
      <button onClick={exportPDF}>Export PDF</button>

      {showPreview && (
        <PreviewPage data={form} close={() => setShowPreview(false)} />
      )}
    </div>
  );
}
