import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import api from "../api";
import "../styles/PreviewPage.css";

export default function PreviewPage() {
  const { state } = useLocation();
  const navigate = useNavigate();

  // if user navigated directly, redirect back to builder
  if (!state || !state.previewHtml) {
    navigate("/", { replace: true });
    return null;
  }

  const { form, summary, previewHtml } = state;

  async function exportPdf() {
    try {
      // try /pdf/export first then fallback to /api/pdf
      let res;
      try {
        res = await api.post("/pdf/export", { name: form.name, email: form.emailId, summary, skills: form.skills, html: previewHtml }, { responseType: "blob" });
      } catch (err) {
        // fallback endpoint
        res = await api.post("/api/pdf", { html: previewHtml }, { responseType: "blob" });
      }

      const blob = new Blob([res.data], { type: "application/pdf" });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${form.name || "resume"}.pdf`;
      document.body.appendChild(a);
      a.click();
      a.remove();
      URL.revokeObjectURL(url);
    } catch (err) {
      console.error("Export PDF error:", err);
      alert("Failed to export PDF. Check backend endpoint or network.");
    }
  }

  return (
    <div className="preview-page-wrapper">
      <div className="preview-header">
        <button className="btn-secondary" onClick={() => navigate(-1)}>‚Üê Back</button>
        <div>
          <button className="btn-primary" onClick={exportPdf}>Export PDF</button>
        </div>
      </div>

      <div className="preview-content-box" dangerouslySetInnerHTML={{ __html: previewHtml }} />
    </div>
  );
}
