import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api"; 
import PersonalDetailsSection from "./ResumeSections/PersonalDetailsSection";
import EducationSection from "./ResumeSections/EducationSection";
import ExperienceSection from "./ResumeSections/ExperienceSection";
import ProjectsSection from "./ResumeSections/ProjectsSection";
import SkillsSection from "./ResumeSections/SkillsSection";
import CertificatesSection from "./ResumeSections/CertificatesSection";
import Navbar from "./Navbar";
const qualificationList = [
  "M.A (Master of Arts)",
  "M.Sc (Master of Science)",
  "M.Com (Master of Commerce)",
  "MBA (Master of Business Administration)",
  "MCA (Master of Computer Applications)",
  "M.Tech (Master of Technology)",
  "M.E (Master of Engineering)",
  "M.Ed (Master of Education)",
  "B.A (Bachelor of Arts)",
  "B.Sc (Bachelor of Science)",
  "B.Com (Bachelor of Commerce)",
  "BBA (Bachelor of Business Administration)",
  "BCA (Bachelor of Computer Applications)",
  "B.Tech (Bachelor of Technology)",
  "B.E (Bachelor of Engineering)",
  "B.Ed (Bachelor of Education)",
  "Diploma",
  "Higher Secondary (HSC)",
  "Secondary School (SSLC)",
  "Other",
];

const ResumeBuilder = () => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: "",
    summary: "",
    education: [
      {
        institute: "",
        startYear: "",
        endYear: "",
        eduType: "",
        eduTypeOther: "",
        department: "",
        score: "",
        scoreType: "CGPA",
      },
    ],
    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [
      {
        name: "",
        description: "",
        technologies: "",
        keyPoints: [""],
        link: "",
      },
    ],
    certificates: [
      { title: "", issuedBy: "", issuedOn: "", credential: "" },
    ],
    skills: [""],
    highestEducation: "",
    highestEducationOther: "",
  });

  const [gensummary, setSummary] = useState("");
  const [summaryLoading, setSummaryLoading] = useState(false);
  const [loading, setLoading] = useState(false);

  // ----------------- helpers -----------------
  const setScalar = (key, value) =>
    setForm((s) => ({
      ...s,
      [key]: value,
    }));

  const updateArrayField = (section, index, field, value) => {
    const copy = [...form[section]];
    if (field === null) {
      copy[index] = value;
    } else {
      copy[index] = { ...copy[index], [field]: value };
    }
    setForm({ ...form, [section]: copy });
  };

  const addRow = (section, template) => {
    setForm((s) => ({ ...s, [section]: [...s[section], template] }));
  };

  const addProjectKeyPoint = (projectIndex) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints =
      projects[projectIndex].keyPoints || [""];
    projects[projectIndex].keyPoints.push("");
    setForm({ ...form, projects });
  };

  const updateProjectKeyPoint = (projectIndex, kpIndex, value) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints[kpIndex] = value;
    setForm({ ...form, projects });
  };

  const handleChange = (section, index, field, value) => {
  // updates simple scalar fields like highestEducation, highestEducationOther
  if (index === null && field === null) {
    setForm((prev) => ({
      ...prev,
      [section]: value,
    }));
    return;
  }

  // updates array fields like education, experience etc.
  setForm((prev) => {
    const updated = [...prev[section]];
    updated[index] = {
      ...updated[index],
      [field]: value,
    };
    return { ...prev, [section]: updated };
  });
};


  const addField = (section, newItem) => {
    setForm((prev) => ({ ...prev, [section]: [...prev[section], newItem] }));
  };

  // ----------------- AI Summary -----------------
  const buildPrompt = () => {
    const skillsText = form.skills.filter(Boolean).join(", ") || "N/A";
    return `
Write a professional resume summary for a ${form.role || "professional"} with ${form.totalExperienceYear || 0} year(s) of experience. 
if the years of experience is 0 then just use as a fresher.
Highlight these skills: ${skillsText}.
Keep it concise (<= 100 words), results-focused, and suitable for a recruiter.
`.trim();
  };

  const generateSummary = async () => {
    setLoading(true);
 
    try {
      const prompt = buildPrompt();
      const res = await api.post("/ai/generate", { prompt });
      const aiSummary =
        res.data?.summary ?? res.data?.result ?? res.data?.data ?? "";
      setSummary(aiSummary);
      return aiSummary;
    } catch (err) {
      console.error("AI generation error:", err?.response?.data ?? err?.message);
      throw err;
    } finally {
      setLoading(false);
    }
  };

  // ----------------- Preview HTML for /preview -----------------
  const buildPreviewHtml = (resolvedSummary) => {
  const clean = (x) => (x && String(x).trim() ? String(x).trim() : "");

  // ====================== Education ======================
  const eduHtml = (form.education || [])
    .filter((e) => clean(e.institute))
    .map(
      (e) => `
    <div class="edu-item">
      <div class="row-space">
        <strong>${clean(e.institute)}</strong>
        <span>${clean(e.startYear)}${clean(e.startYear) || clean(e.endYear) ? " - " : ""}${clean(e.endYear)}</span>
      </div>
      <div class="left-row muted">
        ${clean(e.eduType === "Other" ? e.eduTypeOther : e.eduType)}
        ${clean(e.department) ? ` ‚Äî ${clean(e.department)}` : ""}
        ${clean(e.score) ? ` ‚Äî ${clean(e.score)}` : ""}
      </div>
    </div>`
    )
    .join("");

  // ====================== Experience ======================
  const expHtml = (form.experience || [])
    .filter((e) => clean(e.role) || clean(e.company))
    .map(
      (e) => `
    <div class="exp-item">
      <div class="row-space">
        <strong>${clean(e.role)}</strong>
        <span>${clean(e.duration)}</span>
      </div>
      <div class="left-row muted">${clean(e.company)}</div>
      ${clean(e.activities) ? `<div class="left-row">${clean(e.activities)}</div>` : ""}
    </div>`
    )
    .join("");

  // ====================== Projects ======================
  const projectsHtml = (form.projects || [])
    .filter((p) => clean(p.name))
    .map(
      (p) => `
    <div class="proj-item">
      <div class="row-space">
        <strong>${clean(p.name)}</strong>
        <span>${
          clean(p.link)
            ? `<a href="${clean(p.link)}" target="_blank" rel="noreferrer">${clean(p.link)}</a>`
            : ""
        }</span>
      </div>
      ${clean(p.description) ? `<div class="left-row muted">${clean(p.description)}</div>` : ""}
      ${
        p.keyPoints?.filter((kp) => clean(kp)).length
          ? `<ul class="kp-list left-row">
                ${p.keyPoints.filter((kp) => clean(kp)).map((kp) => `<li>${kp}</li>`).join("")}
             </ul>`
          : ""
      }
      ${
        clean(p.technologies)
          ? `<div class="left-row muted"><strong>Tech Used:</strong> ${clean(p.technologies)}</div>`
          : ""
      }
    </div>`
    )
    .join("");

  // ====================== Certificates (UPDATED) ======================
  const certHtml = (form.certificates || [])
    .filter((c) => clean(c.title))
    .map(
      (c) => `
    <div class="cert-item" style="margin-bottom:10px;">
      <div class="row-space" style="display:flex; justify-content:space-between; font-weight:600;">
        <span>${clean(c.title)}</span>
        <span style="font-size:13px; color:#555;">${clean(c.issuedOn)}</span>
      </div>

      <div class="left-row muted">${clean(c.issuedBy) || ""}</div>

      ${
        clean(c.credential)
          ? `<div class="left-row">
               <a href="${clean(c.credential)}" target="_blank" rel="noreferrer">${clean(c.credential)}</a>
             </div>`
          : ""
      }
    </div>`
    )
    .join("");

  // ====================== Skills ======================
  const skillsHtml = form.skills.filter(Boolean).length
    ? `<div class="skills-wrap">
         ${form.skills.filter(Boolean).map((s) => `<span class="skill-pill">${s}</span>`).join(" ")}
       </div>`
    : "";

  // ====================== FINAL PREVIEW HTML ======================
  return `
    <div class="preview-root">
      <h1 class="center">${clean(form.name) || ""}</h1>
      <div class="center muted">${clean(form.role) || ""}</div>

      <div class="contact center">
        ${clean(form.emailId) ? `üìß ${clean(form.emailId)} ` : ""}
        ${clean(form.phoneNo) ? ` | üìû ${clean(form.phoneNo)} ` : ""}
        ${clean(form.linkedIn) ? ` | üîó ${clean(form.linkedIn)} ` : ""}
        ${clean(form.portfolioLink) ? ` | üåê ${clean(form.portfolioLink)}` : ""}
      </div>

      ${
        resolvedSummary
          ? `<h3 style="font-weight:700;">Professional Summary</h3><hr/>
             <div class="section-body">${resolvedSummary}</div>`
          : ""
      }

      ${eduHtml ? `<h3 style="font-weight:800;">Education</h3><hr/><div class="section-body">${eduHtml}</div>` : ""}
      ${expHtml ? `<h3 style="font-weight:800;">Experience</h3><hr/><div class="section-body">${expHtml}</div>` : ""}
      ${projectsHtml ? `<h3 style="font-weight:700;">Projects</h3><hr/><div class="section-body">${projectsHtml}</div>` : ""}
      ${certHtml ? `<h3 style="font-weight:700;">Certificates</h3><hr/><div class="section-body">${certHtml}</div>` : ""}
      ${skillsHtml ? `<h3 style="font-weight:700;">Skills</h3><hr/><div class="section-body">${skillsHtml}</div>` : ""}
    </div>
  `;
};

  // ----------------- Actions -----------------
  const handlePreview = () => {
    if (!gensummary) {
      alert("Please generate a summary first!");
      return;
    }
    const previewHtml = buildPreviewHtml(gensummary);
    navigate("/preview", { state: { form, gensummary, previewHtml } });
  };

  const saveResumeHandler = async () => {
    try {
      setLoading(true);
      
      const res = await api.post("/portfolio", form);
      console.log(res.data)
      if (res.data?.success) {
        alert("‚úÖ Resume data saved successfully!");
      } else {
        alert("‚ùå Failed to save resume data.");
      }
    } catch (err) {
        console.error("SAVE ERROR:", err.response?.data || err.message);
        alert("‚ùå Error saving resume data.");
      } finally {
      setLoading(false);
    }
  };

  // ----------------- JSX -----------------
  return (
    <>
    <Navbar/>
    
    <div className="max-w-5xl mx-auto my-8 px-4">
      <div className="bg-white border border-gray-100 shadow-sm rounded-xl p-6 md:p-8">
        <header className="flex items-center justify-between gap-4 mb-6">
          <div>
            <h2 className="text-2xl font-semibold text-gray-900">
              AI Resume Builder
            </h2>
            <p className="text-sm text-gray-500 mt-1">
              Fill your details, generate an AI summary, preview & export.
            </p>
          </div>
        </header>

        {/* Personal Details */}
        <PersonalDetailsSection form={form} setScalar={setScalar} />

        {/* Education */}
        <EducationSection
          form={form}
          handleChange={handleChange}
          addField={addField}
          qualificationList={qualificationList}
        />

        {/* Experience */}
        <ExperienceSection
          experience={form.experience}
          updateArrayField={updateArrayField}
          addRow={addRow}
        />

        {/* Projects */}
        <ProjectsSection
          projects={form.projects}
          updateArrayField={updateArrayField}
          addRow={addRow}
          addProjectKeyPoint={addProjectKeyPoint}
          updateProjectKeyPoint={updateProjectKeyPoint}
        />

        {/* Certificates */}
        <CertificatesSection
          certificates={form.certificates}
          updateArrayField={updateArrayField}
          addRow={addRow}
        />

        {/* Skills */}
        <SkillsSection
          skills={form.skills}
          updateArrayField={updateArrayField}
          addRow={addRow}
        />

        {/* Actions */}
        <div className="mt-8 flex flex-col items-center gap-4">
          <button
            className="inline-flex items-center justify-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-50 disabled:opacity-60 disabled:cursor-not-allowed"
            onClick={saveResumeHandler}
            disabled={loading}
          >
            üíæ Save Resume
          </button>

          <div className="flex flex-col items-center gap-2">
            <button
              className="inline-flex items-center justify-center rounded-md bg-blue-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-blue-700 disabled:opacity-60 disabled:cursor-not-allowed"
              onClick={async () => {
                try {
                  setSummaryLoading(true);
                  const aiSummary = await generateSummary();
                  setSummary(aiSummary.replace(/\*/g, ""));
                } catch (err) {
                  alert(
                    "Error generating summary ‚Äî please check network or API key."
                  );
                } finally {
                  setSummaryLoading(false);
                }
              }}
              disabled={loading}
            >
              {gensummary ? "Generate Another Summary" : "Generate Summary"}
            </button>
            <p className="text-xs text-gray-500 h-4">
              {summaryLoading ? "Generating summary..." : ""}
            </p>

            {gensummary && (
              <div className="w-full mt-2 rounded-lg border border-gray-200 bg-gray-50 px-3 py-2 text-sm text-gray-800">
                {gensummary}
              </div>
            )}
          </div>

          <button
            className="inline-flex items-center justify-center rounded-md bg-emerald-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-emerald-700 disabled:opacity-60 disabled:cursor-not-allowed"
            onClick={handlePreview}
            disabled={!gensummary}
          >
            üëÅÔ∏è Preview Resume
          </button>
        </div>
      </div>
    </div>
    </>
  );
};

export default ResumeBuilder;
