import React, { useState } from "react";
import axios from "axios";
import "../styles/ResumeBuilder.css";

const ResumeBuilder = () => {
  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,

    education: [{ institute: "", city: "", year: "", grade: "" }],
    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [
      { name: "", description: "", technologies: "", keyPoints: "", link: "" },
    ],
    certificates: [
      { title: "", issuedBy: "", issuedOn: "", credential: "" },
    ],

    skills: [""],
  });

  const [summary, setSummary] = useState("");
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (section, index, field, value) => {
    if (Array.isArray(form[section])) {
      const updated = [...form[section]];
      if (field !== null) updated[index][field] = value;
      else updated[index] = value;
      setForm({ ...form, [section]: updated });
    } else {
      setForm({ ...form, [section]: value });
    }
  };

  const addField = (section, emptyObj) => {
    setForm({ ...form, [section]: [...form[section], emptyObj] });
  };

  const generateSummary = async () => {
    try {
      setLoading(true);

      const prompt = `
        Write a professional resume summary for a ${form.role} named ${form.name}
        with ${form.totalExperienceYear} year of experience.
        Highlight skills in ${form.skills.join(", ")}.
        Keep it under 100 words.
      `;

      const response = await axios.post(
        "https://ai-resume-builder-backend-u4v2.onrender.com/ai/generate",
        { prompt }
      );

      setSummary(response.data.summary);
    } catch (error) {
      alert("Error generating summary");
    } finally {
      setLoading(false);
    }
  };

  const clean = (text) => (text && text.trim() !== "" ? text : null);

  const buildPreview = () => {
    const eduHTML = form.education
      .filter((e) => clean(e.institute))
      .map(
        (e) => `
        <p>
          <strong>${e.institute}</strong> ‚Äì ${e.city}<br/>
          ${e.year ? `Year: ${e.year}` : ""}<br/>
          ${e.grade ? `Grade: ${e.grade}` : ""}
        </p>`
      )
      .join("");

    const expHTML = form.experience
      .filter((e) => clean(e.role))
      .map(
        (e) => `
        <p>
          <strong>${e.role}</strong> ‚Äì ${e.company}<br/>
          ${e.duration ? `Duration: ${e.duration}` : ""}<br/>
          ${e.activities ? `<em>${e.activities}</em>` : ""}
        </p>`
      )
      .join("");

    const projectHTML = form.projects
      .filter((p) => clean(p.name))
      .map(
        (p) => `
        <p>
          <strong>${p.name}</strong><br/>
          ${p.description}<br/>
          ${p.technologies ? `Tech: ${p.technologies}` : ""}<br/>
          ${p.keyPoints ? `Key: ${p.keyPoints}` : ""}<br/>
          ${p.link ? `<a href="${p.link}" target="_blank">${p.link}</a>` : ""}
        </p>`
      )
      .join("");

    const certHTML = form.certificates
      .filter((c) => clean(c.title))
      .map(
        (c) => `
        <p>
          <strong>${c.title}</strong><br/>
          ${c.issuedBy ? `Issued By: ${c.issuedBy}` : ""}<br/>
          ${c.issuedOn ? `Date: ${c.issuedOn}` : ""}<br/>
          ${
            c.credential
              ? `<a href="${c.credential}" target="_blank">${c.credential}</a>`
              : ""
          }
        </p>`
      )
      .join("");

    const skillsHTML = form.skills
      .filter((s) => clean(s))
      .map((s) => `<span style="margin-right:10px;">‚Ä¢ ${s}</span>`)
      .join("");

    return `
      <div style="font-family: Inter; line-height: 1.6;">
        <h1>${clean(form.name) || ""}</h1>
        <p>${clean(form.role) || ""}</p>

        <p>
          ${clean(form.emailId) ? `üìß ${form.emailId}<br/>` : ""}
          ${clean(form.phoneNo) ? `üìû ${form.phoneNo}<br/>` : ""}
          ${clean(form.linkedIn) ? `üîó ${form.linkedIn}<br/>` : ""}
          ${clean(form.portfolioLink) ? `üåê ${form.portfolioLink}<br/>` : ""}
        </p>

        ${summary ? `<h3>Professional Summary</h3><p>${summary}</p>` : ""}

        ${eduHTML ? `<h3>Education</h3>${eduHTML}` : ""}

        ${expHTML ? `<h3>Experience</h3>${expHTML}` : ""}

        ${projectHTML ? `<h3>Projects</h3>${projectHTML}` : ""}

        ${certHTML ? `<h3>Certificates</h3>${certHTML}` : ""}

        ${skillsHTML ? `<h3>Skills</h3><p>${skillsHTML}</p>` : ""}
      </div>
    `;
  };

  const handlePreview = async () => {
    await generateSummary();
    setPreview(buildPreview());
  };

  const exportPDF = async () => {
    try {
      const response = await axios.post(
        "https://ai-resume-builder-backend-u4v2.onrender.com/api/pdf",
        { html: preview },
        { responseType: "blob" }
      );

      const blob = new Blob([response.data], { type: "application/pdf" });
      const link = document.createElement("a");
      link.href = URL.createObjectURL(blob);
      link.download = `${form.name || "resume"}.pdf`;
      link.click();
    } catch (error) {
      alert("PDF export failed");
    }
  };

  return (
    <div className="resume-wrapper">
      <h2>AI Resume Builder</h2>

      {/* ---------------- BASIC INFO ---------------- */}
      <div className="resume-form">
        <input
          placeholder="Full Name"
          value={form.name}
          onChange={(e) => handleChange("name", null, null, e.target.value)}
        />

        <input
          placeholder="Role / Title"
          value={form.role}
          onChange={(e) => handleChange("role", null, null, e.target.value)}
        />

        <input
          placeholder="Email ID"
          value={form.emailId}
          onChange={(e) => handleChange("emailId", null, null, e.target.value)}
        />

        <input
          placeholder="Phone Number"
          value={form.phoneNo}
          onChange={(e) => handleChange("phoneNo", null, null, e.target.value)}
        />

        <input
          placeholder="LinkedIn URL"
          value={form.linkedIn}
          onChange={(e) => handleChange("linkedIn", null, null, e.target.value)}
        />

        <input
          placeholder="Portfolio Link"
          value={form.portfolioLink}
          onChange={(e) =>
            handleChange("portfolioLink", null, null, e.target.value)
          }
        />

        <input
          type="number"
          placeholder="Total Experience (Years)"
          value={form.totalExperienceYear}
          onChange={(e) =>
            handleChange(
              "totalExperienceYear",
              null,
              null,
              Number(e.target.value)
            )
          }
        />
      </div>

      {/* ---------------- EDUCATION ---------------- */}
      <h3 className="section-title">Education</h3>
      {form.education.map((edu, index) => (
        <div key={index} className="section-box">
          <input
            placeholder="Institute"
            value={edu.institute}
            onChange={(e) =>
              handleChange("education", index, "institute", e.target.value)
            }
          />
          <input
            placeholder="City"
            value={edu.city}
            onChange={(e) =>
              handleChange("education", index, "city", e.target.value)
            }
          />
          <input
            placeholder="Year"
            value={edu.year}
            onChange={(e) =>
              handleChange("education", index, "year", e.target.value)
            }
          />
          <input
            placeholder="Grade"
            value={edu.grade}
            onChange={(e) =>
              handleChange("education", index, "grade", e.target.value)
            }
          />
        </div>
      ))}
      <button
        className="add-btn"
        onClick={() =>
          addField("education", {
            institute: "",
            city: "",
            year: "",
            grade: "",
          })
        }
      >
        + Add Education
      </button>

      {/* ---------------- EXPERIENCE ---------------- */}
      <h3 className="section-title">Experience</h3>
      {form.experience.map((exp, index) => (
        <div key={index} className="section-box">
          <input
            placeholder="Role"
            value={exp.role}
            onChange={(e) =>
              handleChange("experience", index, "role", e.target.value)
            }
          />
          <input
            placeholder="Company"
            value={exp.company}
            onChange={(e) =>
              handleChange("experience", index, "company", e.target.value)
            }
          />
          <input
            placeholder="Duration"
            value={exp.duration}
            onChange={(e) =>
              handleChange("experience", index, "duration", e.target.value)
            }
          />

          <textarea
            placeholder="Activities / Responsibilities"
            value={exp.activities}
            onChange={(e) =>
              handleChange("experience", index, "activities", e.target.value)
            }
          />
        </div>
      ))}
      <button
        className="add-btn"
        onClick={() =>
          addField("experience", {
            role: "",
            company: "",
            duration: "",
            activities: "",
          })
        }
      >
        + Add Experience
      </button>

      {/* ---------------- PROJECTS ---------------- */}
      <h3 className="section-title">Projects</h3>
      {form.projects.map((prj, index) => (
        <div key={index} className="section-box">
          <input
            placeholder="Project Name"
            value={prj.name}
            onChange={(e) =>
              handleChange("projects", index, "name", e.target.value)
            }
          />

          <textarea
            placeholder="Description"
            value={prj.description}
            onChange={(e) =>
              handleChange("projects", index, "description", e.target.value)
            }
          />

          <input
            placeholder="Technologies"
            value={prj.technologies}
            onChange={(e) =>
              handleChange("projects", index, "technologies", e.target.value)
            }
          />

          <input
            placeholder="Key Points"
            value={prj.keyPoints}
            onChange={(e) =>
              handleChange("projects", index, "keyPoints", e.target.value)
            }
          />

          <input
            placeholder="Project Link"
            value={prj.link}
            onChange={(e) =>
              handleChange("projects", index, "link", e.target.value)
            }
          />
        </div>
      ))}
      <button
        className="add-btn"
        onClick={() =>
          addField("projects", {
            name: "",
            description: "",
            technologies: "",
            keyPoints: "",
            link: "",
          })
        }
      >
        + Add Project
      </button>

      {/* ---------------- CERTIFICATES ---------------- */}
      <h3 className="section-title">Certificates</h3>
      {form.certificates.map((cert, index) => (
        <div key={index} className="section-box">
          <input
            placeholder="Certificate Title"
            value={cert.title}
            onChange={(e) =>
              handleChange("certificates", index, "title", e.target.value)
            }
          />
          <input
            placeholder="Issued By"
            value={cert.issuedBy}
            onChange={(e) =>
              handleChange("certificates", index, "issuedBy", e.target.value)
            }
          />
          <input
            placeholder="Issued On"
            value={cert.issuedOn}
            onChange={(e) =>
              handleChange("certificates", index, "issuedOn", e.target.value)
            }
          />
          <input
            placeholder="Credential Link"
            value={cert.credential}
            onChange={(e) =>
              handleChange("certificates", index, "credential", e.target.value)
            }
          />
        </div>
      ))}
      <button
        className="add-btn"
        onClick={() =>
          addField("certificates", {
            title: "",
            issuedBy: "",
            issuedOn: "",
            credential: "",
          })
        }
      >
        + Add Certificate
      </button>

      {/* ---------------- SKILLS ---------------- */}
      <h3 className="section-title">Skills</h3>
      {form.skills.map((skill, index) => (
        <input
          key={index}
          placeholder="Skill"
          className="skill-input"
          value={skill}
          onChange={(e) =>
            handleChange("skills", index, null, e.target.value)
          }
        />
      ))}
      <button className="add-btn" onClick={() => addField("skills", "")}>
        + Add Skill
      </button>

      {/* ---------------- PREVIEW BUTTON ---------------- */}
      <div className="center mt-20">
        <button className="btn-primary" onClick={handlePreview} disabled={loading}>
          {loading ? "Generating..." : "Preview Resume"}
        </button>
      </div>

      {/* ---------------- PREVIEW ---------------- */}
      {preview && (
        <div className="preview-box">
          <h3>Preview</h3>

          <div
            className="preview-content"
            dangerouslySetInnerHTML={{ __html: preview }}
          />

          <div className="center mt-20">
            <button className="btn-secondary" onClick={exportPDF}>
              Export to PDF
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ResumeBuilder;
