import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "../api"; // your axios instance with interceptors
import "../styles/ResumeBuilder.css";

const ResumeBuilder = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    emailId: "",
    linkedIn: "",
    portfolioLink: "",
    phoneNo: "",
    role: "",
    totalExperienceYear: 0,

    education: [{ institute: "", city: "", year: "", grade: "" }],
    experience: [{ role: "", company: "", duration: "", activities: "" }],
    projects: [{ name: "", description: "", technologies: "", keyPoints: [""], link: "" }],
    certificates: [{ title: "", issuedBy: "", issuedOn: "", credential: "" }],
    skills: [""],
  });

  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);

  // Generic handlers
  const setScalar = (key, value) => setForm((s) => ({ ...s, [key]: value }));

  const updateArrayField = (section, index, field, value) => {
    const updated = [...form[section]];
    if (field === null) updated[index] = value;
    else updated[index][field] = value;
    setForm({ ...form, [section]: updated });
  };

  const addRow = (section, template) => {
    setForm({ ...form, [section]: [...form[section], template] });
  };

  const addProjectKeyPoint = (projectIndex) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints.push("");
    setForm({ ...form, projects });
  };

  const updateProjectKeyPoint = (projectIndex, kpIndex, value) => {
    const projects = [...form.projects];
    projects[projectIndex].keyPoints[kpIndex] = value;
    setForm({ ...form, projects });
  };

  // Build the prompt to send to AI
  const buildPrompt = () => {
    return `
Write a professional resume summary for a ${form.role || "professional"} named ${form.name || ""} with ${form.totalExperienceYear || 0} year(s) of experience. 
Highlights (skills): ${form.skills.filter(Boolean).join(", ") || "N/A"}.
Keep it concise (<= 100 words) and focus on achievements and impact.
`.trim();
  };

  // Generate summary via backend (/ai/generate) using api (axios)
  async function generateSummary() {
    setLoading(true);
    try {
      const prompt = buildPrompt();
      const res = await api.post("/ai/generate", { prompt });
      // backend might return different shapes: prefer summary, then result
      const aiSummary = res.data?.summary ?? res.data?.result ?? res.data?.data?.summary ?? "";
      setSummary(aiSummary);
      return aiSummary;
    } catch (err) {
      console.error("AI summary error:", err?.response?.data ?? err.message);
      throw err;
    } finally {
      setLoading(false);
    }
  }

  // Create preview HTML that will be passed to Preview page
  const buildPreviewHtml = (resolvedSummary) => {
  const clean = (x) => (x && String(x).trim() ? String(x).trim() : null);

  /* ================== EDUCATION PREVIEW ================== */
  const eduHtml =
    form.education
      .filter((e) => clean(e.institute))
      .map(
        (e) => `
        <div style="margin-bottom:14px;">
          
          <!-- First Row: Institute - Years -->
          <div style="
            display:flex;
            justify-content:space-between;
            font-weight:600;
            font-size:15px;
          ">
            <span>${e.institute}</span>
            <span>${e.startYear || ""} - ${e.endYear || ""}</span>
          </div>

          <!-- Second Row: Type + Department -->
          <div style="color:#444; font-size:14px; margin:2px 0;">
            ${e.eduType || ""} ${e.department ? `‚Äî ${e.department}` : ""}
          </div>

          <!-- Third Row: Grade -->
          <div style="color:#555; font-size:14px;">
            CGPA / Percentage: ${e.score || "N/A"}
          </div>

        </div>
        `
      )
      .join("") || "";

  /* ================== EXPERIENCE PREVIEW ================== */
  const expHtml =
    form.experience
      .filter((e) => clean(e.role))
      .map(
        (e) => `
        <div style="margin-bottom:14px;">
          
          <!-- Row 1: Role + Duration -->
          <div style="
            display:flex;
            justify-content:space-between;
            font-size:15px;
            font-weight:600;
          ">
            <span>${e.role}</span>
            <span>(${e.duration || ""})</span>
          </div>

          <!-- Row 2: Company -->
          <div style="font-size:14px; color:#444;">
            ${e.company || ""}
          </div>

          <!-- Row 3: Description -->
          <div style="font-size:14px; margin-top:2px; color:#555;">
            ${e.activities || ""}
          </div>
        </div>
      `
      )
      .join("") || "";

  /* ================== PROJECTS PREVIEW ================== */
  const projectsHtml =
    form.projects
      .filter((p) => clean(p.name))
      .map(
        (p) => `
        <div style="margin-bottom:16px;">

          <!-- Row 1: Title + Link -->
          <div style="
            display:flex;
            justify-content:space-between;
            font-weight:600;
            font-size:15px;
          ">
            <span>${p.name}</span>
            <span>
              ${p.link ? `<a href="${p.link}" target="_blank">${p.link}</a>` : ""}
            </span>
          </div>

          <!-- Row 2: Description -->
          <div style="font-size:14px; margin-top:4px; color:#444;">
            ${p.description || ""}
          </div>

          <!-- Row 3: Key Points -->
          ${
            p.keyPoints?.filter((kp) => kp.trim()).length
              ? `
              <ul style="padding-left:20px; font-size:14px; margin:6px 0;">
                ${p.keyPoints
                  .filter((kp) => kp.trim())
                  .map((kp) => `<li>${kp}</li>`)
                  .join("")}
              </ul>`
              : ""
          }

          <!-- Row 4: Tech Used -->
          ${
            p.technologies
              ? `<div style="font-size:14px; color:#444;">
                  <strong>Tech Used:</strong> ${p.technologies}
                </div>`
              : ""
          }
        </div>
      `
      )
      .join("") || "";

  /* ================== CERTIFICATES PREVIEW ================== */
  const certHtml =
    form.certificates
      .filter((c) => clean(c.title))
      .map(
        (c) => `
        <div style="margin-bottom:12px;">
          <strong style="font-size:15px;">${c.title}</strong>
          <div style="font-size:14px; color:#444;">
            ${c.issuedBy || ""}
          </div>
          <div style="font-size:14px; color:#444;">
            ${c.issuedOn || ""}
          </div>
          <div>
            ${
              c.credential
                ? `<a href="${c.credential}" target="_blank">${c.credential}</a>`
                : ""
            }
          </div>
        </div>`
      )
      .join("") || "";

  /* ================== SKILLS PREVIEW ================== */
  const skillsHtml =
    form.skills.filter(Boolean).length
      ? `
    <p style="font-size:14px;">
      ${form.skills.filter(Boolean).map((s) => `‚Ä¢ ${s}`).join(" &nbsp;&nbsp; ")}
    </p>`
      : "";

  /* ================== FINAL HTML ================== */
  return `
    <div style="font-family:Inter; line-height:1.55;">

      <!-- Name + Role (center aligned) -->
      <h1 style="text-align:center; margin:0;">${form.name}</h1>
      <div style="text-align:center; color:#444; margin-bottom:12px;">
        ${form.role || ""}
      </div>

      <!-- Contact Info -->
      <div style="color:#444; margin-bottom:12px; text-align:center;">
        ${form.emailId ? `üìß ${form.emailId} &nbsp;` : ""}
        ${form.phoneNo ? `üìû ${form.phoneNo} &nbsp;` : ""}
        ${form.linkedIn ? `üîó ${form.linkedIn} &nbsp;` : ""}
        ${form.portfolioLink ? `üåê ${form.portfolioLink}` : ""}
      </div>

      <!-- Summary -->
      ${
        resolvedSummary
          ? `
        <h3>Professional Summary</h3>
        <p style="color:#444; margin-bottom:16px;">${resolvedSummary}</p>`
          : ""
      }

      <!-- Education -->
      ${eduHtml ? `<h3>Education</h3>${eduHtml}` : ""}

      <!-- Experience -->
      ${expHtml ? `<h3>Experience</h3>${expHtml}` : ""}

      <!-- Projects -->
      ${projectsHtml ? `<h3>Projects</h3>${projectsHtml}` : ""}

      <!-- Certificates -->
      ${certHtml ? `<h3>Certificates</h3>${certHtml}` : ""}

      <!-- Skills -->
      ${skillsHtml ? `<h3>Skills</h3>${skillsHtml}` : ""}
    </div>
  `;
};


  const handleChange = (section, index, field, value) => {
  // ---- For simple top-level values ----
  if (index === null && field === null) {
    setForm({ ...form, [section]: value });
    return;
  }

  // ---- For array-based sections (education, experience, etc.) ----
  const updated = [...form[section]];
  updated[index][field] = value;

  setForm({ ...form, [section]: updated });
};

const addField = (section, newItem) => {
  setForm((prev) => ({
    ...prev,
    [section]: [...prev[section], newItem],
  }));
};

  // Handle preview click: generate summary then navigate to /preview with router state
  const handlePreview = async () => {
    try {
      setLoading(true);
      const aiSummary = await generateSummary();
      const previewHtml = buildPreviewHtml(aiSummary);
      // pass full form, summary and previewHtml to Preview route via state
      navigate("/preview", { state: { form, summary: aiSummary, previewHtml } });
    } catch (err) {
      // show a user-friendly message
      alert("Error generating Summary ‚Äî please check your network or API key.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="resume-wrapper">
      <h2>AI Resume Builder</h2>

      <section className="resume-section">
        <h3>Personal Details</h3>
        <div className="grid-2">
          <input placeholder="Full Name" value={form.name} onChange={(e) => setScalar("name", e.target.value)} />
          <input placeholder="Role / Title" value={form.role} onChange={(e) => setScalar("role", e.target.value)} />
          <input placeholder="Email ID" value={form.emailId} onChange={(e) => setScalar("emailId", e.target.value)} />
          <input placeholder="Phone Number" value={form.phoneNo} onChange={(e) => setScalar("phoneNo", e.target.value)} />
          <input placeholder="LinkedIn URL" value={form.linkedIn} onChange={(e) => setScalar("linkedIn", e.target.value)} />
          <input placeholder="Portfolio Link" value={form.portfolioLink} onChange={(e) => setScalar("portfolioLink", e.target.value)} />
          <input type="number" placeholder="Total Experience (Years)" value={form.totalExperienceYear} onChange={(e) => setScalar("totalExperienceYear", Number(e.target.value))} />
        </div>
      </section>

      {/* ===================== EDUCATION ===================== */}
<h3 className="section-title">Education</h3>

{/* Highest Qualification Dropdown */}
<div className="section-box">
  <label className="input-label">Highest Qualification</label>
  <select
    value={form.highestEducation || ""}
    onChange={(e) => handleChange("highestEducation", null, null, e.target.value)}
    className="select-input"
  >
    <option value="">Select Highest Qualification</option>
    <option value="MA">MA</option>
    <option value="MBA">MBA</option>
    <option value="MCA">MCA</option>
    <option value="MSc">MSc</option>
    <option value="B.Tech">B.Tech</option>
    <option value="B.Com">B.Com</option>
    <option value="B.Sc">B.Sc</option>
    <option value="Diploma">Diploma</option>
    <option value="12th">Higher Secondary (12th)</option>
    <option value="10th">SSLC / 10th</option>
  </select>
</div>

{/* Multiple Education Blocks */}
{form.education.map((edu, index) => (
  <div key={index} className="section-box">

    {/* Row - Institute + Years */}
    <div className="row-flex">
      <input
        placeholder="Institute Name"
        value={edu.institute}
        onChange={(e) =>
          handleChange("education", index, "institute", e.target.value)
        }
      />

      <input
        placeholder="Start Year"
        value={edu.startYear}
        onChange={(e) =>
          handleChange("education", index, "startYear", e.target.value)
        }
      />

      <input
        placeholder="End Year"
        value={edu.endYear}
        onChange={(e) =>
          handleChange("education", index, "endYear", e.target.value)
        }
      />
    </div>

    {/* Row - Education Type + Department */}
    <div className="row-flex">
      <select
        value={edu.eduType}
        onChange={(e) =>
          handleChange("education", index, "eduType", e.target.value)
        }
      >
        <option value="">Select Education Type</option>
        <option value="Masters">Masters</option>
        <option value="Bachelor">Bachelor</option>
        <option value="Degree">Degree</option>
        <option value="Diploma">Diploma</option>
        <option value="Higher Secondary">Higher Secondary (12th)</option>
        <option value="SSLC">SSLC / 10th</option>
      </select>

      <input
        placeholder="Department / Stream"
        value={edu.department}
        onChange={(e) =>
          handleChange("education", index, "department", e.target.value)
        }
      />

      <input
        placeholder="CGPA / Percentage"
        value={edu.score}
        onChange={(e) =>
          handleChange("education", index, "score", e.target.value)
        }
      />
    </div>
  </div>
))}

<button
  className="add-btn"
  onClick={() =>
    addField("education", {
      institute: "",
      startYear: "",
      endYear: "",
      eduType: "",
      department: "",
      score: ""
    })
  }
>
  + Add Education Entry
</button>


      {/* Experience */}
      <section className="resume-section">
        <h3>Experience</h3>
        {form.experience.map((ex, i) => (
          <div key={i} className="section-box grid-2">
            <input placeholder="Role" value={ex.role} onChange={(e) => updateArrayField("experience", i, "role", e.target.value)} />
            <input placeholder="Company" value={ex.company} onChange={(e) => updateArrayField("experience", i, "company", e.target.value)} />
            <input placeholder="Duration" value={ex.duration} onChange={(e) => updateArrayField("experience", i, "duration", e.target.value)} />
            <input placeholder="Activities" value={ex.activities} onChange={(e) => updateArrayField("experience", i, "activities", e.target.value)} />
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("experience", { role: "", company: "", duration: "", activities: "" })}>+ Add Experience</button>
      </section>

      {/* Projects */}
      <section className="resume-section">
        <h3>Projects</h3>
        {form.projects.map((p, i) => (
          <div key={i} className="project-block">
            <input placeholder="Project Name" value={p.name} onChange={(e) => updateArrayField("projects", i, "name", e.target.value)} />
            <input placeholder="Short Description" value={p.description} onChange={(e) => updateArrayField("projects", i, "description", e.target.value)} />
            <input placeholder="Technologies (comma separated)" value={p.technologies} onChange={(e) => updateArrayField("projects", i, "technologies", e.target.value)} />
            <input placeholder="Project Link" value={p.link} onChange={(e) => updateArrayField("projects", i, "link", e.target.value)} />

            <div className="keypoints">
              <h4>Key Points</h4>
              {p.keyPoints.map((kp, kpi) => (
                <input key={kpi} placeholder="Key point" value={kp} onChange={(e) => updateProjectKeyPoint(i, kpi, e.target.value)} />
              ))}
              <button className="btn-mini" onClick={() => addProjectKeyPoint(i)}>+ Add Key Point</button>
            </div>
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("projects", { name: "", description: "", technologies: "", keyPoints: [""], link: "" })}>+ Add Project</button>
      </section>

      {/* Certificates */}
      <section className="resume-section">
        <h3>Certificates</h3>
        {form.certificates.map((c, i) => (
          <div key={i} className="section-box grid-2">
            <input placeholder="Title" value={c.title} onChange={(e) => updateArrayField("certificates", i, "title", e.target.value)} />
            <input placeholder="Issued By" value={c.issuedBy} onChange={(e) => updateArrayField("certificates", i, "issuedBy", e.target.value)} />
            <input placeholder="Issued On" value={c.issuedOn} onChange={(e) => updateArrayField("certificates", i, "issuedOn", e.target.value)} />
            <input placeholder="Credential Link / ID" value={c.credential} onChange={(e) => updateArrayField("certificates", i, "credential", e.target.value)} />
          </div>
        ))}
        <button className="btn-secondary" onClick={() => addRow("certificates", { title: "", issuedBy: "", issuedOn: "", credential: "" })}>+ Add Certificate</button>
      </section>

      {/* Skills */}
      <section className="resume-section">
        <h3>Skills</h3>
        {form.skills.map((s, i) => (
          <input key={i} placeholder="Skill" value={s} onChange={(e) => updateArrayField("skills", i, null, e.target.value)} />
        ))}
        <button className="btn-secondary" onClick={() => addRow("skills", "")}>+ Add Skill</button>
      </section>

      <div className="center-box">
        <button className="btn-primary" onClick={handlePreview} disabled={loading}>
          {loading ? "Generating..." : "Preview Resume"}
        </button>
      </div>
    </div>
  );
};

export default ResumeBuilder;
